// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library Events {
  /** SpaceFactory events */
  event SpaceCreated(
    uint256 indexed spaceId,
    address spaceAddress,
    address founder,
    string spaceName,
    string symbol,
    uint256 preBuyEthAmount
  );
  event PriceUpdated(uint256 price);
  event FeeReceiverUpdated(address receiver);
  event WithdrawEther(address indexed to, uint256 amount);
  event WithdrawToken(address indexed to, address token, uint256 amount);
  event Swap(address indexed account, address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut);

  /** App events */
  event AppCreated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);
  event AppUpdated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);

  /** space events */
  event StakingFeePercentUpdated(uint256 percent);
  event TokenDeposited(uint256 amount);

  /** Token events */
  event Trade(
    TradeType indexed tradeType,
    address indexed account,
    uint256 ethAmount,
    uint256 tokenAmount,
    uint256 creatorFee,
    uint256 protocolFee
  );

  enum TradeType {
    Buy,
    Sell
  }

  /** Staking events */
  event StakingEvent(StakingType indexed stakingType, address indexed user, uint256 amount);
  event StakingClaimed(address user, uint256 amount);
  event RewardsPerTokenUpdated(uint256 accumulated);
  event UserRewardsUpdated(address user, uint256 rewards, uint256 checkpoint);
  event YieldReleased(uint256 amount);

  enum StakingType {
    Stake,
    Unstake
  }

  /** Member events */
  event Subscribed(uint8 indexed planId, address indexed user, uint256 duration, uint256 tokenAmount);
  event Unsubscribed(uint8 indexed planId, address indexed user, uint256 amount);
  event PlanCreated(uint8 indexed id, string uri, uint256 price);

  /** Share events */
  event RewardsPerShareUpdated(uint256 accumulated);
  event ShareRewardsClaimed(address user, uint256 amount);
  event SharesTransferred(address indexed from, address indexed to, uint256 amount);
  event ContributorAdded(address indexed account);
  event ShareOrderCreated(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderCanceled(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderExecuted(
    uint256 indexed orderId,
    address indexed seller,
    address buyer,
    uint256 amount,
    uint256 price
  );
  event VestingAdded(
    address indexed payer,
    address indexed beneficiary,
    uint256 start,
    uint256 duration,
    uint256 allocation
  );
  event VestingReleased(address indexed payer, address indexed beneficiary, uint256 amount);
}

contracts/lib/Member.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Token.sol";
import "./Events.sol";
import "./Constants.sol";
import "hardhat/console.sol";

library Member {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.Bytes32Set;

  struct State {
    uint8 planIndex;
    uint256 subscriptionIndex;
    uint256 subscriptionIncome;
    mapping(uint8 => Plan) plans;
    mapping(bytes32 => Subscription) subscriptions;
  }

  struct Plan {
    string uri;
    uint256 price; // monthly
    bool isActive;
  }

  struct Subscription {
    uint8 planId;
    address account;
    uint256 startTime;
    uint256 duration;
    uint256 amount; // total amount
  }

  /* Plan */
  function createPlan(State storage self, string memory uri, uint256 price) external returns (uint8) {
    self.plans[self.planIndex] = Plan(uri, price, true);
    self.planIndex++;
    return self.planIndex - 1;
  }

  function updatePlan(State storage self, uint8 id, string memory uri, uint256 price, bool isActive) external {
    require(id < self.planIndex, "Plan is not existed");
    self.plans[id].uri = uri;
    self.plans[id].price = price;
    self.plans[id].isActive = isActive;
  }

  function getPlans(State storage self) external view returns (Plan[] memory plans) {
    uint256 len = self.planIndex;
    plans = new Plan[](len);
    for (uint8 i = 0; i < len; i++) {
      plans[i] = self.plans[i];
    }
  }

  /* ====== Subscription ======= */

  function subscribe(
    State storage self,
    EnumerableSet.Bytes32Set storage subscriptionIds,
    uint8 planId,
    uint256 amount,
    uint256 durationFromAmount,
    bool needTransfer
  ) external returns (uint256 consumedAmount, uint256 remainDuration) {
    bytes32 id = keccak256(abi.encode(planId, msg.sender));
    Subscription storage subscription = self.subscriptions[id];

    if (needTransfer) {
      IERC20(address(this)).safeTransferFrom(msg.sender, address(this), amount);
    }

    // new subscription
    if (subscription.startTime == 0) {
      subscription.planId = planId;
      subscription.account = msg.sender;
      subscriptionIds.add(id);
    }

    (consumedAmount, remainDuration) = distributeSingleSubscription(self, id);

    subscription.startTime = block.timestamp;
    subscription.amount += amount;
    subscription.duration += durationFromAmount;
  }

  function unsubscribe(
    State storage self,
    EnumerableSet.Bytes32Set storage subscriptionIds,
    uint8 planId,
    uint256 amount
  ) external returns (uint256 subscriptionFee, uint256 unsubscribeAmount) {
    bytes32 id = keccak256(abi.encode(planId, msg.sender));
    Subscription storage subscription = self.subscriptions[id];
    require(subscription.startTime > 0, "Subscription not found");
    require(amount > 0, "Amount must be greater than zero");

    (subscriptionFee, ) = distributeSingleSubscription(self, id);

    // Unsubscribe all;
    if (amount >= subscription.amount) {
      IERC20(address(this)).transfer(msg.sender, subscription.amount);
      delete self.subscriptions[id];
      subscriptionIds.remove(id);

      unsubscribeAmount = subscription.amount;
    } else {
      uint256 unsubscribedDuration = (subscription.duration * amount) / subscription.amount;
      subscription.amount -= amount;
      subscription.duration -= unsubscribedDuration;

      IERC20(address(this)).transfer(msg.sender, amount);
      unsubscribeAmount = amount;
    }
  }

  function distributeSingleSubscription(State storage self, bytes32 id) public returns (uint256, uint256) {
    Subscription storage subscription = self.subscriptions[id];
    if (subscription.startTime == 0) return (0, 0);

    (uint256 consumedAmount, uint256 remainDuration) = calculateConsumedAmount(self, id, block.timestamp);

    if (consumedAmount == 0) return (0, 0);

    subscription.startTime = block.timestamp;
    subscription.amount -= consumedAmount;
    subscription.duration = remainDuration;
    // self.subscriptionIncome += consumedAmount;
    return (consumedAmount, remainDuration);
  }

  function getSubscriptions(
    State storage self,
    EnumerableSet.Bytes32Set storage subscriptionIds
  ) external view returns (Subscription[] memory) {
    bytes32[] memory ids = subscriptionIds.values();
    uint256 len = ids.length;
    Subscription[] memory subscriptions = new Subscription[](len);

    for (uint256 i = 0; i < len; i++) {
      subscriptions[i] = self.subscriptions[ids[i]];
    }
    return subscriptions;
  }

  function calculateConsumedAmount(
    State storage self,
    bytes32 id,
    uint256 timestamp
  ) public view returns (uint256, uint256) {
    Subscription memory subscription = self.subscriptions[id];

    /// Subscription not found
    if (subscription.startTime == 0) return (0, 0);

    /** Invalid timestamp */
    if (timestamp < subscription.startTime) return (0, 0);

    uint256 pastDuration = timestamp - subscription.startTime;

    /** Expired, all should be consumed */
    if (pastDuration >= subscription.duration) {
      return (subscription.amount, 0);
    }

    uint256 remainDuration = subscription.duration - pastDuration;

    // calculate consumedAmount by ratio of (pastDuration/duration)
    uint256 consumedAmount = (subscription.amount * pastDuration) / subscription.duration;
    return (consumedAmount, remainDuration);
  }

  function getTokenPricePerSecond(
    State storage self,
    Token.State memory token,
    uint8 planId
  ) internal view returns (uint256) {
    Member.Plan memory plan = self.plans[planId];
    uint256 ethPricePerSecond = plan.price / SECONDS_PER_MONTH;
    BuyInfo memory info = Token.getTokenAmount(token, ethPricePerSecond);
    return info.tokenAmountAfterFee;
  }
}

contracts/lib/Share.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./TransferUtil.sol";
import "./Events.sol";
import "./Constants.sol";
import "hardhat/console.sol";

library Share {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;

  struct Contributor {
    uint256 shares;
    uint256 rewards; // realized rewards (unclaimed)
    uint256 checkpoint;
    bool exists;
  }

  struct ContributorInfo {
    address account;
    uint256 shares;
    uint256 rewards; // realized rewards (unclaimed)
    uint256 checkpoint;
    bool exists;
  }

  struct Order {
    address seller;
    uint256 amount;
    uint256 price;
  }

  struct Vesting {
    address payer;
    uint256 start;
    uint256 duration;
    uint256 allocation; // allocation share amount
    uint256 released; // released share amount
  }

  struct VestingInfo {
    address beneficiary;
    address payer;
    uint256 start;
    uint256 duration;
    uint256 allocation;
    uint256 released;
  }

  struct State {
    uint256 daoFee;
    uint256 accumulatedRewardsPerShare;
    uint256 orderIndex;
    mapping(address => Contributor) contributors;
    mapping(uint256 => Order) orders;
    address[] contributorAddresses;
    mapping(address => Vesting) vestings;
  }

  /** --- share --- */

  function transferShares(State storage self, address to, uint256 amount) external {
    require(self.contributors[msg.sender].exists, "Sender is not a contributor");
    require(self.contributors[msg.sender].shares >= amount, "Insufficient shares");
    require(to != address(0) && msg.sender != to, "Invalid recipient address");

    if (!self.contributors[to].exists) {
      addContributor(self, to);
    } else {
      _updateRewardsPerShare(self);
    }

    self.contributors[msg.sender].shares -= amount;
    self.contributors[to].shares += amount;
  }

  function createShareOrder(
    State storage self,
    EnumerableSet.UintSet storage orderIds,
    uint256 amount,
    uint256 price
  ) external returns (uint256) {
    Contributor storage contributor = self.contributors[msg.sender];
    require(contributor.shares >= amount, "Insufficient share balance");
    require(amount > 0, "Amount must be greater than zero");
    self.orders[self.orderIndex] = Order(msg.sender, amount, price);
    orderIds.add(self.orderIndex);
    self.orderIndex++;
    return self.orderIndex - 1;
  }

  function cancelShareOrder(State storage self, EnumerableSet.UintSet storage orderIds, uint256 orderId) external {
    Order storage order = self.orders[orderId];
    require(order.seller != address(0), "Order not found");
    require(order.seller == msg.sender, "Only seller can cancel order");
    orderIds.remove(orderId);
    delete self.orders[orderId];
  }

  function executeShareOrder(
    State storage self,
    EnumerableSet.UintSet storage orderIds,
    uint256 orderId,
    uint256 amount
  ) external returns (address seller, uint256 price) {
    Order storage order = self.orders[orderId];
    require(order.seller != address(0), "Order not found");
    require(amount <= order.amount, "Amount too large");
    uint256 ethAmount = order.price * amount;
    require(msg.value >= ethAmount, "Insufficient payment");
    require(self.contributors[order.seller].shares >= amount, "Insufficient share of seller");

    TransferUtil.safeTransferETH(order.seller, ethAmount);

    if (!self.contributors[msg.sender].exists) {
      addContributor(self, msg.sender);
    }

    self.contributors[order.seller].shares -= amount;
    self.contributors[msg.sender].shares += amount;

    seller = order.seller;
    price = order.price;

    if (amount == order.amount) {
      orderIds.remove(orderId);
      delete self.orders[orderId];
    } else {
      order.amount -= amount;
    }
  }

  function getShareOrders(
    State storage self,
    EnumerableSet.UintSet storage orderIds
  ) external view returns (Order[] memory) {
    uint256[] memory ids = orderIds.values();
    uint256 len = ids.length;
    Order[] memory orders = new Order[](len);

    for (uint256 i = 0; i < len; i++) {
      orders[i] = self.orders[ids[i]];
    }
    return orders;
  }

  /** --- contributor --- */

  function addContributor(State storage self, address account) public {
    require(!self.contributors[account].exists, "Contributor is existed");
    _updateRewardsPerShare(self);
    self.contributors[account] = Contributor(0, 0, 0, true);
    self.contributorAddresses.push(account);
  }

  function getContributors(State storage self) external view returns (ContributorInfo[] memory) {
    ContributorInfo[] memory info = new ContributorInfo[](self.contributorAddresses.length);
    for (uint256 i = 0; i < self.contributorAddresses.length; i++) {
      Contributor memory contributor = self.contributors[self.contributorAddresses[i]];
      info[i] = ContributorInfo(
        self.contributorAddresses[i],
        contributor.shares,
        contributor.rewards,
        contributor.checkpoint,
        contributor.exists
      );
    }
    return info;
  }

  function claimRewards(State storage self) external returns (uint256 amount) {
    address user = msg.sender;
    _updateRewardsPerShare(self);
    _updateContributorRewards(self, user);

    amount = self.contributors[user].rewards;
    self.contributors[user].rewards = 0;

    IERC20(address(this)).transfer(msg.sender, amount);
  }

  function distribute(State storage self) external {
    _updateRewardsPerShare(self);
  }

  function currentContributorRewards(State storage self, address user) external view returns (uint256) {
    Contributor memory contributor = self.contributors[user];

    uint256 currentAccumulatedRewardsPerShare = _calculateRewardsPerShare(self);

    uint256 rewards = contributor.rewards +
      _calculateContributorRewards(contributor.shares, contributor.checkpoint, currentAccumulatedRewardsPerShare);

    return rewards;
  }

  /** ----- Vesting ------ */

  function addVesting(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses,
    address beneficiary,
    uint256 startTime,
    uint256 duration,
    uint256 allocation
  ) external {
    require(beneficiary != address(0), "Beneficiary is zero address");
    require(!vestingAddresses.contains(beneficiary), "Beneficiary already exists");
    Contributor memory payer = self.contributors[msg.sender];
    require(payer.shares >= allocation, "Allocation too large");

    if (!self.contributors[beneficiary].exists) {
      addContributor(self, beneficiary);
    }

    self.vestings[beneficiary] = Vesting(msg.sender, startTime, duration, allocation, 0);
    vestingAddresses.add(beneficiary);
  }

  function claimVesting(State storage self) external {
    address beneficiary = msg.sender;
    _claimVesting(self, beneficiary);
  }

  function _claimVesting(State storage self, address beneficiary) internal {
    Vesting storage vesting = self.vestings[beneficiary];
    require(vesting.start != 0, "Beneficiary does not exist");

    uint256 releasable = vestedAmount(self, beneficiary, block.timestamp) - vesting.released;

    if (releasable > 0) {
      vesting.released += releasable;
      emit Events.VestingReleased(vesting.payer, beneficiary, releasable);

      require(self.contributors[vesting.payer].shares > releasable, "Insufficient shares");
      self.contributors[vesting.payer].shares -= releasable;
      self.contributors[beneficiary].shares += releasable;
    }
  }

  function vestedAmount(State storage self, address beneficiary, uint256 timestamp) public view returns (uint256) {
    Vesting storage vesting = self.vestings[beneficiary];

    if (timestamp < vesting.start) {
      return 0;
    } else if (timestamp > vesting.start + vesting.duration) {
      return vesting.allocation;
    } else {
      return (vesting.allocation * (timestamp - vesting.start)) / vesting.duration;
    }
  }

  function removeVesting(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses,
    address beneficiary
  ) external {
    Vesting memory vesting = self.vestings[beneficiary];
    require(vesting.start != 0, "Beneficiary does not exist");
    require(vesting.payer == msg.sender, "Only payer can remove vesting");
    _claimVesting(self, beneficiary);
    vestingAddresses.remove(beneficiary);
    delete self.vestings[beneficiary];
  }

  function getVestings(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses
  ) external view returns (VestingInfo[] memory) {
    address[] memory accounts = vestingAddresses.values();
    uint256 len = accounts.length;
    VestingInfo[] memory vestings = new VestingInfo[](len);

    for (uint256 i = 0; i < len; i++) {
      Vesting memory vesting = self.vestings[accounts[i]];
      vestings[i] = VestingInfo(
        accounts[i],
        vesting.payer,
        vesting.start,
        vesting.duration,
        vesting.allocation,
        vesting.released
      );
    }
    return vestings;
  }

  function _updateContributorRewards(State storage self, address user) internal {
    Contributor memory contributor = self.contributors[user];

    // We skip the storage changes if already updated in the same block
    if (contributor.checkpoint == self.accumulatedRewardsPerShare) {
      return;
    }

    // Calculate and update the new value user reserves.
    contributor.rewards += _calculateContributorRewards(
      contributor.shares,
      contributor.checkpoint,
      self.accumulatedRewardsPerShare
    );

    contributor.checkpoint = self.accumulatedRewardsPerShare;
    self.contributors[user] = contributor;
  }

  function _updateRewardsPerShare(State storage self) internal returns (uint256 rewardsPerShare) {
    rewardsPerShare = _calculateRewardsPerShare(self);
    bool isChanged = self.accumulatedRewardsPerShare != rewardsPerShare;
    // console.log('=====isChanged:', isChanged);

    if (isChanged) {
      self.daoFee = 0;
      self.accumulatedRewardsPerShare = rewardsPerShare;
      emit Events.RewardsPerShareUpdated(rewardsPerShare);
    }
  }

  function _calculateContributorRewards(
    uint256 shares,
    uint256 earlierCheckpoint,
    uint256 latterCheckpoint
  ) internal pure returns (uint256) {
    return (shares * (latterCheckpoint - earlierCheckpoint)) / PER_SHARE_PRECISION;
  }

  function _calculateRewardsPerShare(State storage self) internal view returns (uint256) {
    return self.accumulatedRewardsPerShare + (PER_SHARE_PRECISION * self.daoFee) / SHARES_SUPPLY;
  }
}

contracts/lib/SpaceCreator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";
import "../interfaces/ISpaceFactory.sol";
import "../interfaces/ISpace.sol";
import "../Space.sol";
import "./TransferUtil.sol";

library SpaceCreator {
  using SafeERC20 for IERC20;

  function createSpace(
    uint256 price,
    uint256 spaceIndex,
    mapping(address => address[]) storage userSpaces,
    mapping(uint256 spaceId => address) storage spaces,
    mapping(address => address) storage spaceToFounder,
    uint256 appId,
    string calldata spaceName,
    string calldata symbol,
    uint256 preBuyEthAmount
  ) external returns (address) {
    require(msg.value >= price + preBuyEthAmount, "Insufficient payment");

    address founder = msg.sender;
    Space space = new Space(appId, address(this), founder, spaceName, symbol);

    space.initialize();

    uint256 currentSpaceIndex = spaceIndex;
    spaces[currentSpaceIndex] = address(space);
    userSpaces[msg.sender].push(address(space));
    spaceToFounder[address(space)] = founder;

    if (preBuyEthAmount > 0) {
      BuyInfo memory info = space.buy{ value: preBuyEthAmount }(0);
      IERC20(space).transfer(msg.sender, info.tokenAmountAfterFee);
    }
    return address(space);
  }
}

