// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";
import "../interfaces/ISpaceFactory.sol";
import "../interfaces/ISpace.sol";
import "../Space.sol";
import "./Events.sol";
import "./TransferUtil.sol";

library SpaceHelper {
  using SafeERC20 for IERC20;

  function createApp(
    mapping(uint256 => App) storage apps,
    uint256 appIndex,
    string calldata _uri,
    address _feeReceiver,
    uint256 _feePercent
  ) external {
    require(_feeReceiver != address(0), "Invalid feeTo address");
    require(_feePercent <= 0.05 ether, "appFeePercent must be <= 5%");
    apps[appIndex] = App(msg.sender, _uri, _feeReceiver, _feePercent);
  }

  function updateApp(
    mapping(uint256 => App) storage apps,
    uint256 id,
    string calldata _uri,
    address _feeReceiver,
    uint256 _feePercent
  ) external {
    App storage app = apps[id];
    require(app.creator != address(0), "App not existed");
    require(app.creator == msg.sender, "Only creator can update App URI");
    require(_feeReceiver != address(0), "Invalid feeTo address");
    require(_feePercent <= 0.05 ether, "appFeePercent must be <= 5%");
    app.uri = _uri;
    app.feeReceiver = _feeReceiver;
    app.feePercent = _feePercent;
  }

  function swap(
    mapping(address => address) storage spaceToFounder,
    address tokenIn,
    address tokenOut,
    uint256 amountIn,
    uint256 minTokenAmount
  ) external returns (uint256 returnAmount) {
    require(
      isSpace(spaceToFounder, tokenIn) && isSpace(spaceToFounder, tokenOut) && tokenIn != tokenOut,
      "Invalid tokens"
    );
    IERC20(address(tokenIn)).safeTransferFrom(msg.sender, address(this), amountIn);
    IERC20(address(tokenIn)).approve(tokenIn, amountIn);
    SellInfo memory sellInfo = ISpace(tokenIn).sell(amountIn, 0);
    BuyInfo memory buyInfo = ISpace(tokenOut).buy{ value: sellInfo.ethAmount }(minTokenAmount);
    returnAmount = buyInfo.tokenAmountAfterFee + buyInfo.creatorFee + buyInfo.protocolFee;
    IERC20(address(tokenOut)).transfer(msg.sender, returnAmount);
  }

  function withdrawEther(address feeReceiver) external returns (uint256 amount) {
    amount = address(this).balance;
    TransferUtil.safeTransferETH(feeReceiver, amount);
  }

  function withdrawTokens(address feeReceiver, address[] calldata tokens) external {
    for (uint256 i = 0; i < tokens.length; i++) {
      uint256 amount = IERC20(tokens[i]).balanceOf(address(this));
      IERC20(tokens[i]).transfer(feeReceiver, amount);
      emit Events.WithdrawToken(feeReceiver, tokens[i], amount);
    }
  }

  function isSpace(
    mapping(address => address) storage spaceToFounder,
    address spaceAddress
  ) public view returns (bool) {
    return spaceToFounder[spaceAddress] != address(0);
  }
}

contracts/lib/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";
import "./Events.sol";
import "./Constants.sol";

// creator rewards

library Staking {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  struct State {
    uint256 yieldStartTime;
    uint256 yieldAmount; // yield from space
    uint256 yieldReleased;
    uint256 stakingFee; // fee for rewards
    uint256 totalStaked; // Total amount staked
    uint256 accumulatedRewardsPerToken;
    mapping(address => uint256) userStaked;
    mapping(address => UserRewards) userRewards; // Rewards accumulated per user
  }

  struct UserRewards {
    uint256 realized; // realized reward token amount
    // checkpoint to compare with RewardsPerToken.accumulated
    uint256 checkpoint;
  }

  struct Staker {
    address account;
    uint256 staked;
    uint256 realized;
    uint256 checkpoint;
  }

  function stake(State storage self, EnumerableSet.AddressSet storage stakers, uint256 amount) external {
    address user = msg.sender;
    _updateUserRewards(self, user);
    IERC20(address(this)).safeTransferFrom(user, address(this), amount);
    self.totalStaked += amount;
    self.userStaked[user] += amount;
    if (!stakers.contains(user)) stakers.add(user);
  }

  function unstake(State storage self, EnumerableSet.AddressSet storage stakers, uint256 amount) external {
    address user = msg.sender;
    require(amount > 0, "Amount must be greater than zero");
    require(amount <= self.userStaked[user], "Amount too large");

    _updateUserRewards(self, user);
    self.totalStaked -= amount;
    self.userStaked[user] -= amount;
    if (self.userStaked[user] == 0) stakers.remove(user);
    IERC20(address(this)).safeTransfer(user, amount);
  }

  function claim(State storage self) external returns (uint256) {
    address user = msg.sender;
    _updateUserRewards(self, user);

    uint256 amount = self.userRewards[user].realized;
    self.userRewards[user].realized = 0;

    IERC20(address(this)).transfer(msg.sender, amount);
    return amount;
  }

  function getStakers(
    State storage self,
    EnumerableSet.AddressSet storage _stakers
  ) external view returns (Staker[] memory) {
    address[] memory accounts = _stakers.values();
    uint256 len = accounts.length;
    Staker[] memory stakers = new Staker[](len);

    for (uint256 i = 0; i < len; i++) {
      address account = accounts[i];
      stakers[i] = Staker(
        account,
        self.userStaked[account],
        self.userRewards[account].realized,
        self.userRewards[account].checkpoint
      );
    }
    return stakers;
  }

  function currentRewardsPerToken(State storage self) external view returns (uint256) {
    return _calculateRewardsPerToken(self);
  }

  function currentUserRewards(State storage self, address user) external view returns (uint256) {
    UserRewards memory accumulatedRewards = self.userRewards[user];

    uint256 currentAccumulatedRewardsPerToken = _calculateRewardsPerToken(self);

    uint256 rewards = accumulatedRewards.realized +
      _calculateRealizedRewards(
        self.userStaked[user],
        accumulatedRewards.checkpoint,
        currentAccumulatedRewardsPerToken
      );

    return rewards;
  }

  function releasedYieldAmount(State storage self, uint256 timestamp) public view returns (uint256) {
    if (timestamp < self.yieldStartTime) {
      return 0;
    } else if (timestamp > self.yieldStartTime + YIELD_DURATION) {
      return self.yieldAmount;
    } else {
      return (self.yieldAmount * (timestamp - self.yieldStartTime)) / YIELD_DURATION;
    }
  }

  function _releaseYield(State storage self) internal {
    uint256 releasable = releasedYieldAmount(self, block.timestamp) - self.yieldReleased;

    if (releasable > 0 && IERC20(address(this)).balanceOf(address(this)) >= releasable) {
      self.stakingFee += releasable;
      self.yieldReleased += releasable;
      emit Events.YieldReleased(releasable);
    }
  }

  function _calculateRewardsPerToken(State storage self) internal view returns (uint256 rewardsPerToken) {
    if (self.totalStaked == 0) return self.accumulatedRewardsPerToken;
    uint256 releasable = releasedYieldAmount(self, block.timestamp) - self.yieldReleased;
    uint256 stakingFee = self.stakingFee + releasable;
    rewardsPerToken = self.accumulatedRewardsPerToken + (PER_TOKEN_PRECISION * stakingFee) / self.totalStaked;
  }

  function _calculateRealizedRewards(
    uint256 staked,
    uint256 checkpoint,
    uint256 accumulatedRewardsPerToken
  ) internal pure returns (uint256) {
    return (staked * (accumulatedRewardsPerToken - checkpoint)) / PER_TOKEN_PRECISION;
  }

  function _updateRewardsPerToken(State storage self) internal returns (uint256) {
    uint256 rewardsPerToken = _calculateRewardsPerToken(self);

    bool isChanged = self.accumulatedRewardsPerToken != rewardsPerToken;

    // console.log("=========isChanged:", isChanged);
    if (isChanged) {
      self.stakingFee = 0;
      self.accumulatedRewardsPerToken = rewardsPerToken;
      emit Events.RewardsPerTokenUpdated(rewardsPerToken);
    }

    return rewardsPerToken;
  }

  function _updateUserRewards(State storage self, address user) internal returns (UserRewards memory) {
    _releaseYield(self);
    _updateRewardsPerToken(self);
    UserRewards memory userRewards = self.userRewards[user];

    // We skip the storage changes if already updated in the same block
    if (userRewards.checkpoint == self.accumulatedRewardsPerToken) {
      return userRewards;
    }

    // Calculate and update the new value user reserves.
    userRewards.realized += _calculateRealizedRewards(
      self.userStaked[user],
      userRewards.checkpoint,
      self.accumulatedRewardsPerToken
    );

    userRewards.checkpoint = self.accumulatedRewardsPerToken;

    self.userRewards[user] = userRewards;
    emit Events.UserRewardsUpdated(user, userRewards.realized, userRewards.checkpoint);

    return userRewards;
  }
}

contracts/lib/Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";
import "../interfaces/ISpace.sol";
import "./Events.sol";
import "./TransferUtil.sol";

library Token {
  using SafeERC20 for IERC20;

  uint256 public constant CREATOR_FEE_RATE = 0.006 * 1 ether; // 0.6%
  uint256 public constant PROTOCOL_FEE_RATE = 0.004 * 1 ether; // 0.4%

  // initial virtual eth amount
  uint256 public constant initialX = 30 * 1 ether;

  // initial virtual token amount
  uint256 public constant initialY = 1073000191 * 1 ether;

  uint256 public constant initialK = initialX * initialY;

  struct State {
    uint256 x;
    uint256 y;
    uint256 k;
  }

  function getTokenAmount(State memory self, uint256 ethAmount) public pure returns (BuyInfo memory info) {
    info.ethAmount = ethAmount;
    info.newX = self.x + ethAmount;
    info.newY = (self.k + info.newX - 1) / info.newX; // div up
    uint256 tokenAmount = self.y - info.newY;
    info.creatorFee = (tokenAmount * CREATOR_FEE_RATE) / 1 ether;
    info.protocolFee = (tokenAmount * PROTOCOL_FEE_RATE) / 1 ether;
    info.tokenAmountAfterFee = tokenAmount - info.creatorFee - info.protocolFee;
  }

  function getEthAmount(State storage self, uint256 tokenAmount) public view returns (SellInfo memory info) {
    info.creatorFee = (tokenAmount * CREATOR_FEE_RATE) / 1 ether;
    info.protocolFee = (tokenAmount * PROTOCOL_FEE_RATE) / 1 ether;
    info.tokenAmountAfterFee = tokenAmount - info.creatorFee - info.protocolFee;
    info.newY = self.y + info.tokenAmountAfterFee;
    info.newX = (self.k + info.newY - 1) / info.newY; // div up
    info.ethAmount = self.x - info.newX;
  }

  function buy(State storage self, uint256 ethAmount, uint256 minTokenAmount) external returns (BuyInfo memory info) {
    require(ethAmount > 0, "ETH amount must be greater than zero");
    info = getTokenAmount(self, ethAmount);

    require(info.tokenAmountAfterFee >= minTokenAmount, "Slippage too high");

    self.x = info.newX;
    self.y = info.newY;
    self.k = info.newX * info.newY;
  }

  function sell(State storage self, uint256 tokenAmount, uint256 minEthAmount) external returns (SellInfo memory info) {
    require(tokenAmount > 0, "Token amount must be greater than zero");
    info = getEthAmount(self, tokenAmount);

    require(info.ethAmount >= minEthAmount, "Slippage too high");

    IERC20(address(this)).safeTransferFrom(msg.sender, address(this), tokenAmount);

    self.y = info.newY;
    self.x = info.newX;
    self.k = info.newX * info.newY;
  }
}

