pragma solidity ^0.8.22;

// 30 days
uint256 constant SECONDS_PER_MONTH = 24 * 60 * 60 * 30;

uint256 constant DEFAULT_SUBSCRIPTION_PRICE = 0.002048 * 1 ether;

uint256 constant DEFAULT_MIN_SUBSCRIPTION_AMOUNT = 0 * 1 ether;

uint256 constant PER_SHARE_PRECISION = 10 ** 18;

uint256 constant SHARES_SUPPLY = 1_000_000;

uint256 constant PER_TOKEN_PRECISION = 10 ** 26;

// two years
uint256 constant YIELD_DURATION = 24 * 60 * 60 * 365 * 2;

uint256 constant PREMINT_ETH_AMOUNT = 3.3333 ether;
","keccak256":"0x0a4250017b84d277718650c405f4ba06b70be2a5e84ee1d4091c4ece64370491","license":"MIT"},"contracts/lib/Curation.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Events.sol";
import "./Errors.sol";
import "./TransferUtil.sol";
import "../interfaces/ISpace.sol";

library Curation {


  struct State {
    uint256 curatorCount;
    mapping(address => CurationUser) users;
    // @dev mapping of referral code to curator
    mapping(bytes32 => address) curators;
    // @dev mapping of curator to referral code
    mapping(address => bytes32) codes;
    // link between user <> tier
    mapping(address => bool) isActives; // TODO:
    mapping(uint256 => Tier) tiers;
  }

  // create self invitation code
  function createCode(State storage self, bytes32 code) external {
    if (code == bytes32(0)) revert Errors.CodeIsEmpty();
    address account = msg.sender;
    if (self.codes[account] != bytes32(0)) revert Errors.CodeAlreadyExists();

    if (!self.users[account].registered) {
      self.users[account] = CurationUser(address(0), 0, 0, true);
      ++self.curatorCount;
    }
    self.codes[account] = code;
    self.curators[code] = account;
  }

  // update self invitation code
  function updateCode(State storage self, bytes32 code) external {
    if (code == bytes32(0)) revert Errors.CodeIsEmpty();
    if (self.codes[msg.sender] == bytes32(0)) revert Errors.ShouldCreateCodeFirstly();

    if (self.curators[code] != address(0)) revert Errors.CodeIsUsed();

    bytes32 prevCode = self.codes[msg.sender];
    self.curators[code] = msg.sender;
    self.codes[msg.sender] = code;
    delete self.curators[prevCode];
  }

  // bind code
  function bindCode(State storage self, bytes32 code) external {
    if (code == bytes32(0)) revert Errors.CodeIsEmpty();
    if (self.curators[code] == address(0)) revert Errors.CodeNotExists();
    if (self.codes[msg.sender] == code || self.curators[code] == msg.sender) {
      revert Errors.CannotInviteYourself();
    }

    address curator = self.curators[code];

    CurationUser storage me = self.users[msg.sender];
    if (me.curator != address(0)) revert Errors.UserIsInvited();

    if (!me.registered) {
      me.registered = true;
      ++self.curatorCount;
    }
    me.curator = curator;
  }

  function increaseMemberCount(State storage self, address invitee) external {
    CurationUser memory inviteeUser = self.users[invitee];
    if (inviteeUser.curator != address(0)) {
      self.users[inviteeUser.curator].memberCount += 1;
    }
  }

  function decreaseMemberCount(State storage self, address invitee) external {
    CurationUser memory inviteeUser = self.users[invitee];
    if (inviteeUser.curator != address(0)) {
      self.users[inviteeUser.curator].memberCount -= 1;
    }
  }

  function getUser(State storage self, address account) external view returns (CurationUser memory) {
    return self.users[account];
  }

  function getUserByCode(State storage self, bytes32 code) external view returns (CurationUser memory) {
    return self.users[self.curators[code]];
  }

  function getCodeByCurator(State storage self, address curator) external view returns (bytes32) {
    return self.codes[curator];
  }

  function getCuratorByCode(State storage self, bytes32 code) external view returns (address) {
    return self.curators[code];
  }

  function initTiers(State storage self) external {
    self.tiers[0].memberCountBreakpoint = 10;
    self.tiers[0].rebateRate = 0.1 ether; // 10%

    self.tiers[1].memberCountBreakpoint = 20;
    self.tiers[1].rebateRate = 0.2 ether; // 20%

    self.tiers[2].memberCountBreakpoint = 40;
    self.tiers[2].rebateRate = 0.4 ether; // 40%
  }

  function updateTier(State storage self, uint256 id, uint256 memberCountBreakpoint, uint256 rebateRate) external {
    self.tiers[id].memberCountBreakpoint = memberCountBreakpoint;
    self.tiers[id].rebateRate = rebateRate;
  }

  function getTier(State storage self, uint256 id) external view returns (Tier memory) {
    return self.tiers[id];
  }

  function getRebateRate(State storage self, uint256 memberCount) external view returns (uint256) {
    Tier memory tier0 = self.tiers[0];
    if (memberCount <= tier0.memberCountBreakpoint) {
      return tier0.rebateRate;
    }

    Tier memory tier1 = self.tiers[1];
    if (memberCount <= tier1.memberCountBreakpoint) {
      return tier1.rebateRate;
    }
    return self.tiers[2].rebateRate;
  }

  function claimRewards(State storage self) external returns (uint256 rewards) {
    CurationUser storage user = self.users[msg.sender];
    if (user.rewards > 0) {
      IERC20(address(this)).transfer(msg.sender, user.rewards);
      rewards = user.rewards;
      user.rewards = 0;
    }
  }
}
","keccak256":"0x1072f6a022eddf152c5625bec140b16de6fadaef3c8d663376ad48874d384a64","license":"MIT"},"contracts/lib/Errors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

library Errors {
  /** common */
  error AmountIsZero();
  error AmountTooLarge();
  error PriceIsZero();
  error EthAmountIsZero();
  error InsufficientPayment();
  error OnlyCreator();
  error InvalidAddress();

  /** CreationFactory */
  error URIIsEmpty();
  error CreationNotFound();
  error InvalidFeePercent();

  /** SpaceFactory */
  error InvalidAppId();
  error AppNotFound();
  error InvalidFeeReceiver();
  // appFeePercent must be <= 20%
  error ExceedMaxAppFeePercent();
  error InvalidTokens();

  /** Space */
  error OnlyFactory();
  error TokenAmountTooLarge();
  // Staking revenue percent must be >= 10% And <= 100%
  error InvalidStakingRevenuePercent();

  /** Token */
  error SlippageTooHigh();

  /** Member */
  error PlanNotExisted();
  error PlanNotActive();
  error SubscribeAmountTooSmall();
  error ContributorExisted();

  /** Share */
  error OnlyContributor();
  error InsufficientShares();
  error InvalidRecipient();
  error OrderNotFound();
  error OnlySeller();
  error OnlyPayer();
  error ExceedOrderAmount();
  error InvalidBeneficiary();
  error BeneficiaryExists();
  error BeneficiaryNotFound();
  error AllocationTooLarge();

  /** curation */
  error CodeIsEmpty();
  error CodeAlreadyExists();
  error ShouldCreateCodeFirstly();
  error CodeIsUsed();
  error CodeNotExists();
  error CannotInviteYourself();
  error UserIsInvited();
  error SubscriptionNotFound();
}
","keccak256":"0xfa08fa5ae4cf41760a816c982b6d0f227412e7a5c16e74365291f47438f2644e","license":"MIT"},"contracts/lib/Events.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

library Events {
  /** SpaceFactory events */
  event SpaceCreated(
    uint256 indexed spaceId,
    address spaceAddress,
    address founder,
    string spaceName,
    string symbol,
    string uri,
    uint256 preBuyEthAmount,
    address referral
  );
  event PriceUpdated(uint256 price);
  event ReferralRewardRateUpdated(uint256 rate);
  event FeeReceiverUpdated(address receiver);
  event WithdrawEther(address indexed to, uint256 amount);
  event WithdrawToken(address indexed to, address token, uint256 amount);
  event Swap(address indexed account, address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut);

  /** App events */
  event AppCreated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);
  event AppUpdated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);

  /** space events */
  event SpaceConfigUpdated(string uri, uint256 percent);
  event TokenDeposited(uint256 amount);

  /** Token events */
  event Trade(
    TradeType indexed tradeType,
    address indexed account,
    uint256 ethAmount,
    uint256 tokenAmount,
    uint256 creatorFee,
    uint256 protocolFee,
    uint256 balance
  );

  enum TradeType {
    Buy,
    Sell
  }

  /** Staking events */
  event Staked(address indexed account, uint256 amount);
  event Unstaked(address indexed account, uint256 amount);
  event StakingClaimed(address account, uint256 amount);
  event RewardsPerTokenUpdated(uint256 accumulated);
  event UserRewardsUpdated(address account, uint256 rewards, uint256 checkpoint);
  event YieldReleased(uint256 amount);

  /** Member events */
  event Subscribed(
    uint8 indexed planId,
    bool isUsingEth,
    address indexed account,
    uint256 tokenAmount,
    uint256 changedDuration,
    uint256 remainingDuration,
    string uri
  );
  event Unsubscribed(
    uint8 indexed planId,
    address indexed account,
    uint256 tokenAmount,
    uint256 changedDuration,
    uint256 remainingDuration
  );
  event PlanCreated(uint8 indexed id, string uri, uint256 price, uint256 minEthAmount);
  event PlanUpdated(uint8 indexed id, string uri, uint256 price, uint256 minEthAmount);
  event DistributeSubscriptionRewards(address caller, uint256 mintPastDuration);
  event DistributeSingleSubscription(uint8 indexed planId, address account);

  /** Share events */
  event RewardsPerShareUpdated(uint256 accumulated);
  event ShareRewardsClaimed(address account, uint256 amount);
  event ShareRewardsDistributed(address account);
  event SharesTransferred(address indexed from, address indexed to, uint256 amount);
  event ContributorAdded(address indexed account);
  event ShareOrderCreated(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderCanceled(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderExecuted(
    uint256 indexed orderId,
    address indexed seller,
    address buyer,
    uint256 amount,
    uint256 price
  );
  event VestingAdded(
    address indexed payer,
    address indexed beneficiary,
    uint256 start,
    uint256 duration,
    uint256 allocation
  );
  event VestingRemoved(address indexed payer, address indexed beneficiary);
  event VestingReleased(address indexed payer, address indexed beneficiary, uint256 amount);
  event VestingClaimed(address indexed beneficiary, uint256 amount);

  /** Curation events */
  event CodeCreated(address indexed account, bytes32 _code);
  event CodeUpdated(address indexed account, bytes32 _code);
  event CodeBound(address indexed account, bytes32 _code);
  event TierUpdated(uint256 indexed id, uint256 memberCountBreakpoint, uint256 rebateRate);
  event CurationRewardsClaimed(address account, uint256 amount);
}
