pragma solidity ^0.8.22;

library Events {
  /** SpaceFactory events */
  event SpaceCreated(
    uint256 indexed spaceId,
    address spaceAddress,
    address founder,
    string spaceName,
    string symbol,
    string uri,
    uint256 preBuyEthAmount,
    address referral
  );
  event PriceUpdated(uint256 price);
  event ReferralRewardRateUpdated(uint256 rate);
  event FeeReceiverUpdated(address receiver);
  event WithdrawEther(address indexed to, uint256 amount);
  event WithdrawToken(address indexed to, address token, uint256 amount);
  event Swap(address indexed account, address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut);

  /** App events */
  event AppCreated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);
  event AppUpdated(uint256 id, address indexed creator, string uri, address feeReceiver, uint256 feePercent);

  /** space events */
  event SpaceConfigUpdated(string uri, uint256 percent);
  event TokenDeposited(uint256 amount);

  /** Token events */
  event Trade(
    TradeType indexed tradeType,
    address indexed account,
    uint256 ethAmount,
    uint256 tokenAmount,
    uint256 creatorFee,
    uint256 protocolFee,
    uint256 balance
  );

  enum TradeType {
    Buy,
    Sell
  }

  /** Staking events */
  event Staked(address indexed account, uint256 amount);
  event Unstaked(address indexed account, uint256 amount);
  event StakingClaimed(address account, uint256 amount);
  event RewardsPerTokenUpdated(uint256 accumulated);
  event UserRewardsUpdated(address account, uint256 rewards, uint256 checkpoint);
  event YieldReleased(uint256 amount);

  /** Member events */
  event Subscribed(
    uint8 indexed planId,
    bool isUsingEth,
    address indexed account,
    uint256 tokenAmount,
    uint256 changedDuration,
    uint256 remainingDuration,
    string uri
  );
  event Unsubscribed(
    uint8 indexed planId,
    address indexed account,
    uint256 tokenAmount,
    uint256 changedDuration,
    uint256 remainingDuration
  );
  event PlanCreated(uint8 indexed id, string uri, uint256 price, uint256 minEthAmount);
  event PlanUpdated(uint8 indexed id, string uri, uint256 price, uint256 minEthAmount);
  event DistributeSubscriptionRewards(address caller, uint256 mintPastDuration);
  event DistributeSingleSubscription(uint8 indexed planId, address account);

  /** Share events */
  event RewardsPerShareUpdated(uint256 accumulated);
  event ShareRewardsClaimed(address account, uint256 amount);
  event ShareRewardsDistributed(address account);
  event SharesTransferred(address indexed from, address indexed to, uint256 amount);
  event ContributorAdded(address indexed account);
  event ShareOrderCreated(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderCanceled(uint256 indexed orderId, address indexed seller, uint256 amount, uint256 price);
  event ShareOrderExecuted(
    uint256 indexed orderId,
    address indexed seller,
    address buyer,
    uint256 amount,
    uint256 price
  );
  event VestingAdded(
    address indexed payer,
    address indexed beneficiary,
    uint256 start,
    uint256 duration,
    uint256 allocation
  );
  event VestingRemoved(address indexed payer, address indexed beneficiary);
  event VestingReleased(address indexed payer, address indexed beneficiary, uint256 amount);
  event VestingClaimed(address indexed beneficiary, uint256 amount);

  /** Curation events */
  event CodeCreated(address indexed account, bytes32 _code);
  event CodeUpdated(address indexed account, bytes32 _code);
  event CodeBound(address indexed account, bytes32 _code);
  event TierUpdated(uint256 indexed id, uint256 memberCountBreakpoint, uint256 rebateRate);
  event CurationRewardsClaimed(address account, uint256 amount);
}
","keccak256":"0xfbd5ca9b184ebba95bde0127a85ec940841001f36f90666719c4ca1af90ebcac","license":"MIT"},"contracts/lib/Member.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Token.sol";
import "./Curation.sol";
import "./Events.sol";
import "./Constants.sol";

library Member {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.Bytes32Set;

  struct State {
    uint8 planIndex;
    uint256 subscriptionIndex;
    uint256 subscriptionIncome;
    mapping(uint8 => Plan) plans;
    mapping(bytes32 => Subscription) subscriptions;
  }

  /* Plan */
  function createPlan(
    State storage self,
    string calldata uri,
    uint256 price,
    uint256 minEthAmount
  ) external returns (uint8 planId) {
    if (price == 0) revert Errors.PriceIsZero();
    planId = self.planIndex;
    self.plans[planId] = Plan(uri, price, minEthAmount, true);
    ++self.planIndex;
  }

  function updatePlan(
    State storage self,
    uint8 id,
    string calldata uri,
    uint256 price,
    uint256 minEthAmount,
    bool isActive
  ) external {
    if (price == 0) revert Errors.PriceIsZero();
    if (id >= self.planIndex) revert Errors.PlanNotExisted();
    self.plans[id].uri = uri;
    self.plans[id].price = price;
    self.plans[id].minEthAmount = minEthAmount;
    self.plans[id].isActive = isActive;
  }

  function getPlans(State storage self) external view returns (Plan[] memory plans) {
    uint256 len = self.planIndex;
    plans = new Plan[](len);
    for (uint8 i = 0; i < len; i++) {
      plans[i] = self.plans[i];
    }
  }

  /* ====== Subscription ======= */

  function subscribe(
    State storage self,
    Token.State memory token,
    Curation.State storage curation,
    EnumerableSet.Bytes32Set storage subscriptionIds,
    uint8 planId,
    string calldata uri,
    uint256 tokenAmount
  ) external returns (uint256 increasingDuration, uint256 consumedAmount, uint256 remainingDuration) {
    if (planId >= self.planIndex) revert Errors.PlanNotExisted();
    if (tokenAmount == 0) revert Errors.EthAmountIsZero();

    Plan memory plan = self.plans[planId];
    if (!plan.isActive) revert Errors.PlanNotActive();

    uint256 minimumSubscriptionTokens = calculateMinimumSubscriptionTokens(token, plan);
    if (tokenAmount < minimumSubscriptionTokens) revert Errors.SubscribeAmountTooSmall();

    bytes32 id = generateSubscriptionId(planId, msg.sender);
    Subscription storage subscription = self.subscriptions[id];

    // Calculate the subscription duration
    increasingDuration = calculateIncreasingDuration(token, plan, tokenAmount);

    // Initialize subscription if it does not exist
    if (subscription.startTime == 0) {
      subscription.planId = planId;
      subscription.account = msg.sender;
      subscriptionIds.add(id);
      remainingDuration = increasingDuration;

      Curation.increaseMemberCount(curation, msg.sender);
    } else {
      // Calculate consumed amount and remaining duration
      (consumedAmount, remainingDuration) = distributeSingleSubscription(self, curation, subscriptionIds, id);
    }

    // Update subscription details
    subscription.uri = uri;
    subscription.startTime = block.timestamp;
    subscription.amount += tokenAmount;
    subscription.duration += increasingDuration;
  }

  function unsubscribe(
    State storage self,
    Curation.State storage curation,
    EnumerableSet.Bytes32Set storage subscriptionIds,
    uint8 planId,
    uint256 amount
  )
    external
    returns (uint256 consumedAmount, uint256 unsubscribeAmount, uint256 unsubscribedDuration, uint256 remainingDuration)
  {
    if (amount == 0) revert Errors.AmountIsZero();

    bytes32 id = generateSubscriptionId(planId, msg.sender);
    Subscription storage subscription = self.subscriptions[id];
    if (subscription.startTime == 0) revert Errors.SubscriptionNotFound();

    (consumedAmount, remainingDuration) = distributeSingleSubscription(self, curation, subscriptionIds, id);

    // Calculate the amount to transfer
    uint256 transferAmount = amount >= subscription.amount ? subscription.amount : amount;
    IERC20(address(this)).transfer(msg.sender, transferAmount);

    if (amount >= subscription.amount) {
      // Unsubscribe completely
      delete self.subscriptions[id];
      subscriptionIds.remove(id);
      unsubscribeAmount = subscription.amount;
      unsubscribedDuration = subscription.duration;
    } else {
      // Partially unsubscribe
      unsubscribedDuration = (subscription.duration * amount) / subscription.amount;
      subscription.amount -= amount;
      subscription.duration -= unsubscribedDuration;
      unsubscribeAmount = amount;
    }
  }

  function distributeSingleSubscription(
    State storage self,
    Curation.State storage curation,
    EnumerableSet.Bytes32Set storage subscriptionIds,
    bytes32 id
  ) public returns (uint256, uint256) {
    Subscription storage subscription = self.subscriptions[id];
    if (subscription.startTime == 0) return (0, 0);

    (uint256 consumedAmount, uint256 remainingDuration) = calculateConsumedAmount(self, id, block.timestamp);

    if (consumedAmount == 0) return (0, 0);

    /** expired */
    if (subscription.startTime + subscription.duration <= block.timestamp) {
      Curation.decreaseMemberCount(curation, subscription.account);
      delete self.subscriptions[id];
      subscriptionIds.remove(id);
    } else {
      subscription.startTime = block.timestamp;
      subscription.amount -= consumedAmount;
      subscription.duration = remainingDuration;
    }

    return (consumedAmount, remainingDuration);
  }

  function getSubscription(
    State storage self,
    uint8 planId,
    address account
  ) external view returns (Subscription memory) {
    bytes32 id = generateSubscriptionId(planId, account);
    return self.subscriptions[id];
  }

  function getSubscriptions(
    State storage self,
    EnumerableSet.Bytes32Set storage subscriptionIds
  ) external view returns (Subscription[] memory) {
    bytes32[] memory ids = subscriptionIds.values();
    uint256 len = ids.length;
    Subscription[] memory subscriptions = new Subscription[](len);

    for (uint256 i = 0; i < len; i++) {
      subscriptions[i] = self.subscriptions[ids[i]];
    }
    return subscriptions;
  }

  function calculateConsumedAmount(
    State storage self,
    bytes32 id,
    uint256 timestamp
  ) public view returns (uint256, uint256) {
    Subscription memory subscription = self.subscriptions[id];

    if (subscription.startTime == 0) return (0, 0);

    /** Invalid timestamp */
    if (timestamp < subscription.startTime) return (0, 0);

    uint256 pastDuration = timestamp - subscription.startTime;

    /** Expired, all should be consumed */
    if (pastDuration >= subscription.duration) {
      return (subscription.amount, 0);
    }

    uint256 remainingDuration = subscription.duration - pastDuration;

    // calculate consumedAmount by ratio of (pastDuration/duration)
    uint256 consumedAmount = (subscription.amount * pastDuration) / subscription.duration;
    return (consumedAmount, remainingDuration);
  }

  function calculateIncreasingDuration(
    Token.State memory token,
    Plan memory plan,
    uint256 tokenAmount
  ) internal pure returns (uint256 duration) {
    uint256 ethPricePerSecond = plan.price / SECONDS_PER_MONTH;
    BuyInfo memory info = Token.getTokenAmount(token, ethPricePerSecond);
    duration = tokenAmount / info.tokenAmountAfterFee;
  }

  function calculateMinimumSubscriptionTokens(
    Token.State memory token,
    Plan memory plan
  ) internal pure returns (uint256) {
    uint256 ethPricePerSecond = plan.minEthAmount / SECONDS_PER_MONTH;
    BuyInfo memory info = Token.getTokenAmount(token, ethPricePerSecond);
    return info.tokenAmountAfterFee * SECONDS_PER_MONTH;
  }

  function generateSubscriptionId(uint8 planId, address account) public pure returns (bytes32) {
    return keccak256(abi.encode(planId, account));
  }
}
","keccak256":"0xabe67c8ad7caa15ce2f42276daa1f9578918b2754da1c92b4f8631264813789b","license":"MIT"},"contracts/lib/Share.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./TransferUtil.sol";
import "./Events.sol";
import "./Errors.sol";
import "./Constants.sol";
import "../interfaces/ISpace.sol";

library Share {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;

  struct Order {
    address seller;
    uint256 amount;
    uint256 price;
  }

  struct Vesting {
    address payer;
    uint256 start;
    uint256 duration;
    uint256 allocation; // allocation share amount
    uint256 released; // released share amount
  }

  struct State {
    uint256 daoRevenue;
    uint256 accumulatedRewardsPerShare;
    uint256 orderIndex;
    mapping(address => Contributor) contributors;
    mapping(uint256 => Order) orders;
    address[] contributorAddresses;
    mapping(address => Vesting) vestings;
  }

  /** --- share --- */

  function transferShares(State storage self, address to, uint256 amount) external {
    Contributor memory contributor = self.contributors[msg.sender];
    if (contributor.account == address(0)) {
      revert Errors.OnlyContributor();
    }
    if (contributor.shares < amount) {
      revert Errors.InsufficientShares();
    }
    if (to == address(0) || msg.sender == to) {
      revert Errors.InvalidRecipient();
    }

    if (self.contributors[to].account == address(0)) {
      addContributor(self, to);
    } else {
      _updateRewardsPerShare(self);
    }

    self.contributors[msg.sender].shares -= amount;
    self.contributors[to].shares += amount;
  }

  function createShareOrder(
    State storage self,
    EnumerableSet.UintSet storage orderIds,
    uint256 amount,
    uint256 price
  ) external returns (uint256 orderId) {
    Contributor storage contributor = self.contributors[msg.sender];
    if (contributor.shares < amount) revert Errors.InsufficientShares();
    if (amount == 0) revert Errors.AmountIsZero();
    orderId = self.orderIndex;
    self.orders[orderId] = Order(msg.sender, amount, price);
    orderIds.add(orderId);
    self.orderIndex++;
  }

  function cancelShareOrder(
    State storage self,
    EnumerableSet.UintSet storage orderIds,
    uint256 orderId
  ) external returns (uint256 amount, uint256 price) {
    Order storage order = self.orders[orderId];
    if (order.seller == address(0)) revert Errors.OrderNotFound();
    if (order.seller != msg.sender) revert Errors.OnlySeller();
    amount = order.amount;
    price = order.price;
    orderIds.remove(orderId);
    delete self.orders[orderId];
  }

  function executeShareOrder(
    State storage self,
    EnumerableSet.UintSet storage orderIds,
    uint256 orderId,
    uint256 amount
  ) external returns (address seller, uint256 price) {
    Order storage order = self.orders[orderId];
    if (order.seller == address(0)) revert Errors.OrderNotFound();
    if (amount > order.amount) revert Errors.ExceedOrderAmount();
    uint256 ethAmount = order.price * amount;
    if (msg.value < ethAmount) revert Errors.InsufficientPayment();
    if (self.contributors[order.seller].shares < amount) {
      revert Errors.InsufficientShares();
    }

    TransferUtil.safeTransferETH(order.seller, ethAmount);

    if (self.contributors[msg.sender].account == address(0)) {
      addContributor(self, msg.sender);
    }

    self.contributors[order.seller].shares -= amount;
    self.contributors[msg.sender].shares += amount;

    seller = order.seller;
    price = order.price;

    if (amount == order.amount) {
      orderIds.remove(orderId);
      delete self.orders[orderId];
    } else {
      order.amount -= amount;
    }
  }

  function getShareOrders(
    State storage self,
    EnumerableSet.UintSet storage orderIds
  ) external view returns (OrderInfo[] memory) {
    uint256[] memory ids = orderIds.values();
    uint256 len = ids.length;
    OrderInfo[] memory orders = new OrderInfo[](len);

    for (uint256 i = 0; i < len; i++) {
      Order memory order = self.orders[ids[i]];
      orders[i] = OrderInfo(ids[i], order.seller, order.amount, order.price);
    }
    return orders;
  }

  /** --- contributor --- */

  function addContributor(State storage self, address account) public {
    if (self.contributors[account].account != address(0)) {
      revert Errors.ContributorExisted();
    }
    _updateRewardsPerShare(self);
    self.contributors[account] = Contributor(account, 0, 0, 0);
    self.contributorAddresses.push(account);
  }

  function getContributors(State storage self) external view returns (Contributor[] memory) {
    Contributor[] memory info = new Contributor[](self.contributorAddresses.length);
    for (uint256 i = 0; i < self.contributorAddresses.length; i++) {
      info[i] = self.contributors[self.contributorAddresses[i]];
    }
    return info;
  }

  function claimRewards(State storage self) external returns (uint256 amount) {
    address account = msg.sender;
    _updateRewardsPerShare(self);
    _updateContributorRewards(self, account);

    amount = self.contributors[account].rewards;
    self.contributors[account].rewards = 0;

    IERC20(address(this)).transfer(msg.sender, amount);
  }

  function distribute(State storage self) external {
    _updateRewardsPerShare(self);
  }

  function currentContributorRewards(State storage self, address account) external view returns (uint256) {
    Contributor memory contributor = self.contributors[account];

    uint256 currentAccumulatedRewardsPerShare = _calculateRewardsPerShare(self);

    uint256 rewards = contributor.rewards +
      _calculateContributorRewards(contributor.shares, contributor.checkpoint, currentAccumulatedRewardsPerShare);

    return rewards;
  }

  /** ----- Vesting ------ */

  function addVesting(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses,
    address beneficiary,
    uint256 startTime,
    uint256 duration,
    uint256 allocation
  ) external {
    if (beneficiary == address(0) || beneficiary == msg.sender) {
      revert Errors.InvalidBeneficiary();
    }
    if (vestingAddresses.contains(beneficiary)) {
      revert Errors.BeneficiaryExists();
    }

    Contributor memory payer = self.contributors[msg.sender];

    if (payer.shares < allocation) {
      revert Errors.AllocationTooLarge();
    }

    if (self.contributors[beneficiary].account == address(0)) {
      addContributor(self, beneficiary);
    }

    self.vestings[beneficiary] = Vesting(msg.sender, startTime, duration, allocation, 0);
    vestingAddresses.add(beneficiary);
  }

  function claimVesting(State storage self) external returns (uint256) {
    return _claimVesting(self, msg.sender);
  }

  function _claimVesting(State storage self, address beneficiary) internal returns (uint256 releasable) {
    Vesting storage vesting = self.vestings[beneficiary];
    if (vesting.start == 0) revert Errors.BeneficiaryNotFound();

    releasable = vestedAmount(self, beneficiary, block.timestamp) - vesting.released;

    if (releasable > 0) {
      vesting.released += releasable;
      emit Events.VestingReleased(vesting.payer, beneficiary, releasable);

      if (self.contributors[vesting.payer].shares <= releasable) {
        revert Errors.InsufficientShares();
      }

      self.contributors[vesting.payer].shares -= releasable;
      self.contributors[beneficiary].shares += releasable;
    }
  }

  function vestedAmount(State storage self, address beneficiary, uint256 timestamp) public view returns (uint256) {
    Vesting memory vesting = self.vestings[beneficiary];

    if (timestamp < vesting.start) {
      return 0;
    } else if (timestamp > vesting.start + vesting.duration) {
      return vesting.allocation;
    } else {
      return (vesting.allocation * (timestamp - vesting.start)) / vesting.duration;
    }
  }

  function removeVesting(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses,
    address beneficiary
  ) external {
    Vesting memory vesting = self.vestings[beneficiary];
    if (vesting.start == 0) revert Errors.BeneficiaryNotFound();
    if (vesting.payer != msg.sender) revert Errors.OnlyPayer();
    _claimVesting(self, beneficiary);
    vestingAddresses.remove(beneficiary);
    delete self.vestings[beneficiary];
  }

  function getVestings(
    State storage self,
    EnumerableSet.AddressSet storage vestingAddresses
  ) external view returns (VestingInfo[] memory) {
    address[] memory accounts = vestingAddresses.values();
    uint256 len = accounts.length;
    VestingInfo[] memory vestings = new VestingInfo[](len);

    for (uint256 i = 0; i < len; i++) {
      Vesting memory vesting = self.vestings[accounts[i]];
      vestings[i] = VestingInfo(
        accounts[i],
        vesting.payer,
        vesting.start,
        vesting.duration,
        vesting.allocation,
        vesting.released
      );
    }
    return vestings;
  }

  function _updateContributorRewards(State storage self, address account) internal {
    Contributor memory contributor = self.contributors[account];

    if (contributor.checkpoint == self.accumulatedRewardsPerShare) {
      return;
    }

    contributor.rewards += _calculateContributorRewards(
      contributor.shares,
      contributor.checkpoint,
      self.accumulatedRewardsPerShare
    );

    contributor.checkpoint = self.accumulatedRewardsPerShare;
    self.contributors[account] = contributor;
  }

  function _updateRewardsPerShare(State storage self) internal returns (uint256 rewardsPerShare) {
    rewardsPerShare = _calculateRewardsPerShare(self);
    bool isChanged = self.accumulatedRewardsPerShare != rewardsPerShare;
    if (isChanged) {
      self.daoRevenue = 0;
      self.accumulatedRewardsPerShare = rewardsPerShare;
      emit Events.RewardsPerShareUpdated(rewardsPerShare);
    }
  }

  function _calculateContributorRewards(
    uint256 shares,
    uint256 earlierCheckpoint,
    uint256 latterCheckpoint
  ) internal pure returns (uint256) {
    return (shares * (latterCheckpoint - earlierCheckpoint)) / PER_SHARE_PRECISION;
  }

  function _calculateRewardsPerShare(State storage self) internal view returns (uint256) {
    return self.accumulatedRewardsPerShare + (PER_SHARE_PRECISION * self.daoRevenue) / SHARES_SUPPLY;
  }
}
