","keccak256":"0x7c772354afc50296fb0d39a0d5123c7fada8439398b38dfb546f689d3d35e5b2","license":"MIT"},"contracts/lib/SpaceHelper.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/ISpaceFactory.sol";
import "../interfaces/ISpace.sol";
import "./Member.sol";
import "./Share.sol";
import "./Staking.sol";
import "./Curation.sol";
import "./Events.sol";
import "./Errors.sol";
import "./TransferUtil.sol";

library SpaceHelper {
  using SafeERC20 for IERC20;

  function initialize(
    Member.State storage member,
    Share.State storage share,
    Curation.State storage curation,
    Staking.State storage staking,
    Token.State storage token,
    address factory,
    address owner
  ) external returns (uint8 planId, uint256 premint) {
    if (msg.sender != factory) revert Errors.OnlyFactory();

    Share.addContributor(share, owner);

    share.contributors[owner].shares = SHARES_SUPPLY;

    Curation.initTiers(curation);

    planId = Member.createPlan(member, "", DEFAULT_SUBSCRIPTION_PRICE, DEFAULT_MIN_SUBSCRIPTION_AMOUNT);

    token.x = Token.initialX;
    token.y = Token.initialY;
    token.k = Token.initialK;

    BuyInfo memory info = Token.buy(token, PREMINT_ETH_AMOUNT, 0);

    premint = info.tokenAmountAfterFee + info.creatorFee + info.protocolFee;
    staking.yieldAmount = premint;
    staking.yieldStartTime = block.timestamp;
  }

  function swap(
    mapping(address => address) storage spaceToFounder,
    address tokenIn,
    address tokenOut,
    uint256 amountIn,
    uint256 minTokenAmount
  ) external returns (uint256 returnAmount) {
    if (!isSpace(spaceToFounder, tokenIn) || !isSpace(spaceToFounder, tokenOut) || tokenIn == tokenOut) {
      revert Errors.InvalidTokens();
    }
    IERC20(address(tokenIn)).safeTransferFrom(msg.sender, address(this), amountIn);
    IERC20(address(tokenIn)).approve(tokenIn, amountIn);
    SellInfo memory sellInfo = ISpace(tokenIn).sell(amountIn, 0);
    BuyInfo memory buyInfo = ISpace(tokenOut).buy{ value: sellInfo.ethAmount }(minTokenAmount);
    returnAmount = buyInfo.tokenAmountAfterFee + buyInfo.creatorFee + buyInfo.protocolFee;
    IERC20(address(tokenOut)).transfer(msg.sender, returnAmount);
  }

  function withdrawEther(address feeReceiver) external returns (uint256 amount) {
    amount = address(this).balance;
    TransferUtil.safeTransferETH(feeReceiver, amount);
  }

  function withdrawTokens(address feeReceiver, address[] calldata tokens) external {
    for (uint256 i = 0; i < tokens.length; i++) {
      uint256 amount = IERC20(tokens[i]).balanceOf(address(this));
      IERC20(tokens[i]).transfer(feeReceiver, amount);
      emit Events.WithdrawToken(feeReceiver, tokens[i], amount);
    }
  }

  function isSpace(
    mapping(address => address) storage spaceToFounder,
    address spaceAddress
  ) public view returns (bool) {
    return spaceToFounder[spaceAddress] != address(0);
  }

  function sell(
    Token.State storage token,
    address factory,
    uint256 tokenAmount,
    uint256 minReturnAmount
  ) external returns (SellInfo memory info) {
    info = Token.sell(token, tokenAmount, minReturnAmount);
    if (address(this).balance <= info.ethAmount) revert Errors.TokenAmountTooLarge();

    IERC20(address(this)).transfer(factory, info.protocolFee);
    TransferUtil.safeTransferETH(msg.sender, info.ethAmount);
  }

  // deduct protocolFee and appFee
  function deductSubscriptionFees(
    Member.State storage member,
    address factory,
    uint256 appId,
    uint256 subscriptionFeePercent,
    uint256 revenue
  ) public returns (uint256 creatorRevenue) {
    uint256 appFee = 0;
    App memory app = ISpaceFactory(factory).getApp(appId);

    appFee = (revenue * app.feePercent) / 1 ether;
    uint256 protocolFee = (revenue * subscriptionFeePercent) / 1 ether;
    creatorRevenue = revenue - protocolFee - appFee;
    member.subscriptionIncome += creatorRevenue;
    IERC20(address(this)).transfer(factory, protocolFee);
    if (appFee > 0) {
      IERC20(address(this)).transfer(app.feeReceiver, appFee);
    }
  }

  function distributeCreatorRevenue(
    Staking.State storage staking,
    Share.State storage share,
    uint256 stakingRevenuePercent,
    uint256 creatorRevenue
  ) internal {
    if (staking.totalStaked > 0) {
      uint256 stakingRevenue = (creatorRevenue * stakingRevenuePercent) / 1 ether;
      uint256 daoRevenue = creatorRevenue - stakingRevenue;
      staking.stakingRevenue += stakingRevenue;
      share.daoRevenue += daoRevenue;
    } else {
      share.daoRevenue += creatorRevenue;
    }
  }

  function processSubscriptionRevenue(
    Member.State storage member,
    Share.State storage share,
    Curation.State storage curation,
    Staking.State storage staking,
    Config storage config,
    address factory,
    uint256 appId,
    uint256 revenue,
    address account
  ) external {
    if (revenue > 0) {
      uint256 creatorRevenue = deductSubscriptionFees(member, factory, appId, config.subscriptionFeePercent, revenue);

      CurationUser memory user = curation.users[account];

      if (user.curator != address(0)) {
        CurationUser storage curatorUser = curation.users[user.curator];
        uint256 rebateRate = Curation.getRebateRate(curation, curatorUser.memberCount);

        uint256 rewards = (creatorRevenue * rebateRate) / 1 ether;
        curatorUser.rewards += rewards;
        creatorRevenue = creatorRevenue - rewards;
      }

      SpaceHelper.distributeCreatorRevenue(staking, share, config.stakingRevenuePercent, creatorRevenue);
    }
  }
}
","keccak256":"0xc0ad26ef546ba8605dd09e3c7ca61e999cb9d212dc7ba469b31e4c595f0f7e79","license":"MIT"},"contracts/lib/Staking.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Events.sol";
import "./Errors.sol";
import "./Constants.sol";
import "../interfaces/ISpace.sol";

// creator rewards

library Staking {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  struct State {
    uint256 yieldStartTime;
    uint256 yieldAmount; // yield from space
    uint256 yieldReleased; // released yield
    uint256 stakingRevenue; // fee for rewards
    uint256 totalStaked; // Total amount staked
    uint256 accumulatedRewardsPerToken;
    mapping(address => uint256) userStaked;
    mapping(address => UserRewards) userRewards; // Rewards accumulated per user
  }

  struct UserRewards {
    uint256 realized; // realized reward token amount
    // checkpoint to compare with RewardsPerToken.accumulated
    uint256 checkpoint;
  }

  function stake(State storage self, uint256 amount) external {
    address account = msg.sender;
    _updateUserRewards(self, account);
    IERC20(address(this)).safeTransferFrom(account, address(this), amount);
    self.totalStaked += amount;
    self.userStaked[account] += amount;
  }

  function unstake(State storage self, uint256 amount) external {
    address account = msg.sender;
    if (amount == 0) revert Errors.AmountIsZero();
    if (amount > self.userStaked[account]) revert Errors.AmountTooLarge();

    _updateUserRewards(self, account);
    self.totalStaked -= amount;
    self.userStaked[account] -= amount;
    IERC20(address(this)).safeTransfer(account, amount);
  }

  function claim(State storage self) external returns (uint256) {
    address account = msg.sender;
    _updateUserRewards(self, account);

    uint256 amount = self.userRewards[account].realized;
    self.userRewards[account].realized = 0;

    IERC20(address(this)).transfer(msg.sender, amount);
    return amount;
  }

  function getStaker(State storage self, address account) external view returns (Staker memory) {
    return
      Staker(
        account,
        self.userStaked[account],
        self.userRewards[account].realized,
        self.userRewards[account].checkpoint
      );
  }

  function currentUserRewards(State storage self, address account) external view returns (uint256) {
    UserRewards memory accumulatedRewards = self.userRewards[account];

    uint256 yieldReleasable = releasedYieldAmount(self, block.timestamp) - self.yieldReleased;

    uint256 currentAccumulatedRewardsPerToken = _calculateRewardsPerToken(self, yieldReleasable);

    uint256 rewards = accumulatedRewards.realized +
      _calculateRealizedRewards(
        self.userStaked[account],
        accumulatedRewards.checkpoint,
        currentAccumulatedRewardsPerToken
      );

    return rewards;
  }

  function releasedYieldAmount(State storage self, uint256 timestamp) public view returns (uint256) {
    if (timestamp > self.yieldStartTime + YIELD_DURATION) {
      return self.yieldAmount;
    }

    return (self.yieldAmount * (timestamp - self.yieldStartTime)) / YIELD_DURATION;
  }

  function _releaseYield(State storage self) internal {
    if (self.yieldReleased == self.yieldAmount) return;

    uint256 releasable = releasedYieldAmount(self, block.timestamp) - self.yieldReleased;

    if (releasable == 0) return;
    if (IERC20(address(this)).balanceOf(address(this)) >= releasable) {
      self.stakingRevenue += releasable;
      self.yieldReleased += releasable;
      emit Events.YieldReleased(releasable);
    }
  }

  function _calculateRewardsPerToken(
    State storage self,
    uint256 yieldReleasable
  ) internal view returns (uint256 rewardsPerToken) {
    if (self.totalStaked == 0) return self.accumulatedRewardsPerToken;
    uint256 stakingRevenue = self.stakingRevenue + yieldReleasable;
    rewardsPerToken = self.accumulatedRewardsPerToken + (PER_TOKEN_PRECISION * stakingRevenue) / self.totalStaked;
  }

  function _calculateRealizedRewards(
    uint256 staked,
    uint256 checkpoint,
    uint256 accumulatedRewardsPerToken
  ) internal pure returns (uint256) {
    return (staked * (accumulatedRewardsPerToken - checkpoint)) / PER_TOKEN_PRECISION;
  }

  function _updateRewardsPerToken(State storage self) internal returns (uint256) {
    uint256 rewardsPerToken = _calculateRewardsPerToken(self, 0);

    bool isChanged = self.accumulatedRewardsPerToken != rewardsPerToken;

    if (isChanged) {
      self.stakingRevenue = 0;
      self.accumulatedRewardsPerToken = rewardsPerToken;
      emit Events.RewardsPerTokenUpdated(rewardsPerToken);
    }

    return rewardsPerToken;
  }

  function _updateUserRewards(State storage self, address account) internal returns (UserRewards memory) {
    _releaseYield(self);
    _updateRewardsPerToken(self);
    UserRewards memory userRewards = self.userRewards[account];

    if (userRewards.checkpoint == self.accumulatedRewardsPerToken) {
      return userRewards;
    }

    userRewards.realized += _calculateRealizedRewards(
      self.userStaked[account],
      userRewards.checkpoint,
      self.accumulatedRewardsPerToken
    );

    userRewards.checkpoint = self.accumulatedRewardsPerToken;

    self.userRewards[account] = userRewards;
    emit Events.UserRewardsUpdated(account, userRewards.realized, userRewards.checkpoint);

    return userRewards;
  }
}
