pragma solidity ^0.8.20;

interface IIndieX {
  struct UpsertAppInput {
    string uri;
    address feeTo;
    uint256 appFeePercent;
    uint256 creatorFeePercent;
  }

  struct App {
    uint256 id;
    address creator;
    string uri;
    address feeTo;
    uint256 appFeePercent;
    uint256 creatorFeePercent;
  }

  struct NewCreationInput {
    uint256 appId;
    string uri;
    uint256 curatorFeePercent;
    Curve curve;
    uint8 farmer;
    bool isFarming;
  }

  struct UpdateCreationInput {
    string uri;
    uint256 curatorFeePercent;
  }

  struct Creation {
    uint256 id;
    uint256 appId;
    address creator;
    string uri;
    uint256 curatorFeePercent;
    Curve curve;
    uint8 farmer;
    bool isFarming;
    uint256 balance;
    uint256 volume;
  }

  struct Curve {
    uint96 basePrice;
    uint32 inflectionPoint;
    uint128 inflectionPrice;
    uint128 linearPriceSlope;
  }

  struct PriceInfo {
    uint256 priceAfterFee;
    uint256 price;
    uint256 creatorFee;
    uint256 appFee;
    uint256 protocolFee;
  }

  event NewApp(
    uint256 id,
    address indexed creator,
    string uri,
    address feeTo,
    uint256 appFeePercent,
    uint256 creatorFeePercent
  );

  event UpdateApp(
    uint256 id,
    address indexed creator,
    string uri,
    address feeTo,
    uint256 appFeePercent,
    uint256 creatorFeePercent
  );

  event NewCreation(
    uint256 indexed creationId,
    address indexed creator,
    uint256 indexed appId,
    string uri,
    Curve curve,
    uint8 farmerId,
    bool isFarming
  );

  event UpdateCreation(uint256 indexed creationId, address indexed creator, uint256 indexed appId, string uri);

  event Trade(
    TradeType indexed tradeType,
    uint256 indexed creationId,
    address indexed sender,
    uint256 tokenAmount,
    uint256 fundAmount,
    uint256 creatorFee,
    uint256 appFee,
    uint256 protocolFee,
    address curator,
    uint256 curatorFee
  );

  event FarmerAdded(uint8 indexed farmerIndex, address indexed farmer);
  event ProtocolFeeToUpdated(address indexed previousFeeTo, address indexed newFeeTo);
  event ProtocolFeePercentUpdated(uint256 previousFeePercent, uint256 newFeePercent);

  enum TradeType {
    Mint,
    Buy,
    Sell
  }

  function newApp(UpsertAppInput memory input) external;

  function updateApp(uint256 id, UpsertAppInput memory input) external;

  function newCreation(NewCreationInput memory input) external returns (uint256);

  function updateCreation(uint256 id, UpdateCreationInput memory input) external;

  function buy(uint256 creationId, uint32 amount, address curator) external payable;

  function sell(uint256 creationId, uint32 amount) external;

  function getBuyPrice(uint256 creationId, uint32 amount) external view returns (uint256);

  function getSellPrice(uint256 creationId, uint32 amount) external view returns (uint256);

  function getBuyPriceAfterFee(
    uint256 creationId,
    uint32 amount,
    uint256 appId
  ) external view returns (PriceInfo memory);

  function getSellPriceAfterFee(
    uint256 creationId,
    uint32 amount,
    uint256 appId
  ) external view returns (PriceInfo memory);

  function getApp(uint256 id) external view returns (App memory);

  function getCreation(uint256 id) external view returns (Creation memory);

  function getUserCreations(address creator) external view returns (uint256[] memory);

  function creationSupply(uint256 id) external view returns (uint256);

  function getUserLatestCreation(address creator) external view returns (Creation memory creation);

  function getSubTotal(Creation memory creation, uint32 fromSupply, uint32 quantity) external pure returns (uint256);
}
","keccak256":"0xfb2209c8cc29d4b81c98aded69b6230a965c0c3d697cf1e7fa23bad8dbd14187","license":"MIT"},"contracts/lib/Share.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IIndieX.sol";
import "../lib/TransferUtil.sol";
import "hardhat/console.sol";

library Share {
  uint256 public constant PER_SHARE_PRECISION = 10 ** 18;

  struct Collaborator {
    uint256 share;
    uint256 rewards; // realized rewards
    uint256 checkpoint;
  }

  struct UpsertCollaboratorInput {
    address account;
    uint256 share;
  }

  struct State {
    uint256 daoFees;
    uint256 totalShare;
    uint256 accumulatedRewardsPerShare;
    mapping(address => Collaborator) collaborators;
    address[] collaboratorAddresses;
  }

  event RewardsPerShareUpdated(uint256 accumulated);
  event Claimed(address user, uint256 amount);

  function addCollaborator(State storage self, UpsertCollaboratorInput calldata input) external {
    _updateRewardsPerShare(self);
    self.collaborators[input.account] = Collaborator(input.share, 0, 0);
  }

  function upsertCollaborators(State storage self, UpsertCollaboratorInput[] calldata _collaborators) external {
    _updateRewardsPerShare(self);

    for (uint i = 0; i < _collaborators.length; i++) {
      address account = _collaborators[i].account;
      uint256 share = _collaborators[i].share;

      require(account != address(0), "Invalid address");
      require(share > 0, "Share must be positive");
      if (self.collaborators[account].share == 0) {
        self.collaborators[account] = Collaborator(0, 0, 0);
        self.collaboratorAddresses.push(account);
      }
      _updateCollaboratorRewards(self, account);

      self.collaborators[account].share = share;

      uint256 previousShare = self.collaborators[account].share;
      bool isAdd = share > previousShare;
      if (isAdd) {
        self.totalShare += (share - previousShare);
      } else {
        self.totalShare -= (previousShare - share);
      }
    }
  }

  function getCollaborators(State storage self) public view returns (address[] memory, Collaborator[] memory) {
    Collaborator[] memory allCollaborators = new Collaborator[](self.collaboratorAddresses.length);
    for (uint256 i = 0; i < self.collaboratorAddresses.length; i++) {
      allCollaborators[i] = self.collaborators[self.collaboratorAddresses[i]];
    }
    return (self.collaboratorAddresses, allCollaborators);
  }

  function claim(State storage self) public returns (uint256) {
    address user = msg.sender;
    _updateCollaboratorRewards(self, user);

    uint256 amount = self.collaborators[user].rewards;
    self.collaborators[user].rewards = 0;

    TransferUtil.safeTransferETH(user, amount);

    emit Claimed(user, amount);
    return amount;
  }

  function distribute(State storage self) public {
    _updateRewardsPerShare(self);
  }

  function currentCollaboratorRewards(State storage self, address user) public view returns (uint256) {
    Collaborator memory collaborator = self.collaborators[user];

    uint256 currentAccumulatedRewardsPerShare = _calculateRewardsPerShare(self);

    uint256 rewards = collaborator.rewards +
      _calculateCollaboratorRewards(collaborator.share, collaborator.checkpoint, currentAccumulatedRewardsPerShare);

    return rewards;
  }

  function _updateCollaboratorRewards(State storage self, address user) internal {
    Collaborator memory _collaborator = self.collaborators[user];

    // We skip the storage changes if already updated in the same block
    if (_collaborator.checkpoint == self.accumulatedRewardsPerShare) {
      return;
    }

    // Calculate and update the new value user reserves.
    _collaborator.rewards += _calculateCollaboratorRewards(
      _collaborator.share,
      _collaborator.checkpoint,
      self.accumulatedRewardsPerShare
    );

    _collaborator.checkpoint = self.accumulatedRewardsPerShare;

    self.collaborators[user] = _collaborator;
  }

  function _updateRewardsPerShare(State storage self) internal returns (uint256) {
    uint256 rewardsPerShareOut = _calculateRewardsPerShare(self);

    bool isChanged = self.accumulatedRewardsPerShare != rewardsPerShareOut;

    if (isChanged) {
      self.daoFees = 0;
    }

    self.accumulatedRewardsPerShare = rewardsPerShareOut;

    emit RewardsPerShareUpdated(rewardsPerShareOut);

    return rewardsPerShareOut;
  }

  function _calculateCollaboratorRewards(
    uint256 share,
    uint256 earlierCheckpoint,
    uint256 latterCheckpoint
  ) internal pure returns (uint256) {
    return (share * (latterCheckpoint - earlierCheckpoint)) / PER_SHARE_PRECISION;
  }

  function _calculateRewardsPerShare(State storage self) internal view returns (uint256) {
    if (self.totalShare == 0) return self.accumulatedRewardsPerShare;
    return self.accumulatedRewardsPerShare + (PER_SHARE_PRECISION * self.daoFees) / self.totalShare;
  }
}
","keccak256":"0xd168905ba3daffb0225c3903a45d660cd9b6c5773b721472a507248c64d29d5d","license":"MIT"},"contracts/lib/Staking.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";
import "./TransferUtil.sol";

library Staking {
  using SafeERC20 for IERC20;

  uint256 public constant PER_TOKEN_PRECISION = 10 ** 18;

  struct Info {
    uint256 stakingFees;
    uint256 totalStaked;
    uint256 accumulatedRewardsPerToken;
  }

  struct State {
    uint256 stakingFees; // fee for rewards
    uint256 totalStaked; // Total amount staked
    uint256 accumulatedRewardsPerToken;
    mapping(address => uint256) userStake; // Amount staked per user
    mapping(address => UserRewards) accumulatedRewards; // Rewards accumulated per user
  }

  struct UserRewards {
    uint256 accumulated; // realized reward token amount
    // checkpoint to compare with RewardsPerToken.accumulated
    uint256 checkpoint;
  }

  event Staked(address user, uint256 amount);
  event Unstaked(address user, uint256 amount);
  event Claimed(address user, uint256 amount);
  event RewardsPerTokenUpdated(uint256 accumulated);
  event UserRewardsUpdated(address user, uint256 rewards, uint256 checkpoint);
  event Received(address sender, uint256 stakingFee);

  function _calculateRewardsPerToken(State storage self) internal view returns (uint256) {
    if (self.totalStaked == 0) return self.accumulatedRewardsPerToken;
    return self.accumulatedRewardsPerToken + (PER_TOKEN_PRECISION * self.stakingFees) / self.totalStaked;
  }

  /// @notice Calculate the rewards accumulated by a stake between two checkpoints.
  function _calculateUserRewards(
    uint256 stake_,
    uint256 earlierCheckpoint,
    uint256 latterCheckpoint
  ) internal pure returns (uint256) {
    return (stake_ * (latterCheckpoint - earlierCheckpoint)) / PER_TOKEN_PRECISION;
  }

  function _updateRewardsPerToken(State storage self) internal returns (uint256) {
    uint256 rewardsPerTokenOut = _calculateRewardsPerToken(self);

    bool isChanged = self.accumulatedRewardsPerToken != rewardsPerTokenOut;

    if (isChanged) {
      self.stakingFees = 0;
    }

    self.accumulatedRewardsPerToken = rewardsPerTokenOut;

    emit RewardsPerTokenUpdated(rewardsPerTokenOut);

    return rewardsPerTokenOut;
  }

  function _updateUserRewards(State storage self, address user) internal returns (UserRewards memory) {
    _updateRewardsPerToken(self);
    UserRewards memory userRewards_ = self.accumulatedRewards[user];

    // We skip the storage changes if already updated in the same block
    if (userRewards_.checkpoint == self.accumulatedRewardsPerToken) return userRewards_;

    // Calculate and update the new value user reserves.
    userRewards_.accumulated += _calculateUserRewards(
      self.userStake[user],
      userRewards_.checkpoint,
      self.accumulatedRewardsPerToken
    );

    userRewards_.checkpoint = self.accumulatedRewardsPerToken;

    self.accumulatedRewards[user] = userRewards_;
    emit UserRewardsUpdated(user, userRewards_.accumulated, userRewards_.checkpoint);

    return userRewards_;
  }

  /// @notice Stake tokens.
  function stake(State storage self, uint256 amount) public {
    address user = msg.sender;
    _updateUserRewards(self, user);
    self.totalStaked += amount;
    self.userStake[user] += amount;
    IERC20(address(this)).safeTransferFrom(user, address(this), amount);
    emit Staked(user, amount);
  }

  /// @notice Unstake tokens.
  function unstake(State storage self, uint256 amount) public {
    address user = msg.sender;
    _updateUserRewards(self, user);
    self.totalStaked -= amount;
    self.userStake[user] -= amount;
    IERC20(address(this)).safeTransfer(user, amount);
    emit Unstaked(user, amount);
  }

  /// @notice Claim all rewards for the caller.
  function claim(State storage self) public returns (uint256) {
    address user = msg.sender;
    _updateUserRewards(self, user);

    uint256 amount = self.accumulatedRewards[user].accumulated;
    self.accumulatedRewards[user].accumulated = 0;

    TransferUtil.safeTransferETH(user, amount);

    emit Claimed(user, amount);
    return amount;
  }

  function distribute(State storage self) public {
    _updateRewardsPerToken(self);
  }

  /// @notice Calculate and return current rewards per token.
  function currentRewardsPerToken(State storage self) public view returns (uint256) {
    return _calculateRewardsPerToken(self);
  }

  /// @notice Calculate and return current rewards for a user.
  /// @dev This repeats the logic used on transactions, but doesn't update the storage.
  function currentUserRewards(State storage self, address user) public view returns (uint256) {
    UserRewards memory _accumulatedRewards = self.accumulatedRewards[user];

    uint256 currentAccumulatedRewardsPerToken = _calculateRewardsPerToken(self);

    uint256 rewards = _accumulatedRewards.accumulated +
      _calculateUserRewards(self.userStake[user], _accumulatedRewards.checkpoint, currentAccumulatedRewardsPerToken);

    return rewards;
  }
}
","keccak256":"0x282452725db40b18f426717e4e586d75e9d21ddfb936836105e43e80b2965b8f","license":"MIT"},"contracts/lib/TransferUtil.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library TransferUtil {
  function safeTransferETH(address to, uint256 value) internal {
    (bool success, ) = to.call{ value: value }("");
    require(success, "ETH transfer failed");
  }
}
","keccak256":"0xd47b2af45a6ff307edd8f05a1be01d1abca82412db63b8e3bdb011da8dee80b0","license":"MIT"},"contracts/space/Space.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../lib/TransferUtil.sol";
import "../lib/Share.sol";
import "../lib/Staking.sol";
import "../interfaces/IIndieX.sol";

contract Space is ERC20, ERC20Permit, ERC1155Holder, ReentrancyGuard {
  event Trade(
    TradeType indexed tradeType,
    address indexed account,
    uint256 ethAmount,
    uint256 tokenAmount,
    uint256 fee
  );

  enum TradeType {
    Buy,
    Sell
  }

  uint256 public constant k = 32190005730 * 1 ether * 1 ether;

  // initial virtual eth amount
  uint256 public constant initialX = 30 * 1 ether;
  // initial virtual token amount
  uint256 public constant initialY = 1073000191 * 1 ether;

  uint256 public x = initialX;
  uint256 public y = initialY;

  uint256 public constant FEE_RATE = 1; // 1%

  address public immutable founder;

  // indieX;
  address public immutable indieX;

  // space info
  uint256 creationId;
  uint256 sponsorCreationId;

  struct SpaceInfo {
    string name;
    address founder;
    uint256 creationId;
    uint256 sponsorCreationId;
  }

  // fees
  uint256 public daoFeePercent = 0.5 ether; // 50%

  event Received(address sender, uint256 daoFee, uint256 stakingFee);

  // share
  Share.State share;

  // staking
  Staking.State staking;

  constructor(
    address _indieX,
    address _founder,
    string memory _name,
    string memory _symbol
  ) ERC20(_name, _symbol) ERC20Permit(_name) {
    indieX = _indieX;
    founder = _founder;
  }

  modifier onlyFounder() {
    require(msg.sender == founder, "Only Founder");
    _;
  }

  fallback() external payable {}

  receive() external payable {
    uint256 fees = msg.value;
    uint256 feeToDao = (fees * daoFeePercent) / 1 ether;
    uint256 feeToStaking = fees - feeToDao;

    share.daoFees += feeToDao;
    staking.stakingFees += feeToStaking;
    emit Received(msg.sender, feeToDao, feeToStaking);
  }

  function _initCreation(
    IIndieX.NewCreationInput calldata creationInput,
    IIndieX.NewCreationInput calldata sponsorCreationInput
  ) internal {
    creationId = IIndieX(indieX).newCreation(creationInput);
    sponsorCreationId = IIndieX(indieX).newCreation(sponsorCreationInput);

    uint256[] memory ids = new uint256[](2);
    ids[0] = creationId;
    ids[1] = sponsorCreationId;

    uint256[] memory amounts = new uint256[](2);
    amounts[0] = 1;
    amounts[1] = 1;

    IERC1155(indieX).safeBatchTransferFrom(address(this), founder, ids, amounts, "");
  }

  function initialize(
    IIndieX.NewCreationInput calldata creationInput,
    IIndieX.NewCreationInput calldata sponsorCreationInput
  ) external {
    // StakingRewards _stakingRewards = new StakingRewards(token);
    // stakingRewards = address(_stakingRewards);

    _initCreation(creationInput, sponsorCreationInput);
  }

  function buy() public payable nonReentrant {
    uint256 ethAmount = msg.value;
    require(ethAmount > 0, "ETH amount must be greater than zero");

    uint256 fee = (ethAmount * FEE_RATE) / 100;
    uint256 ethAmountAfterFee = ethAmount - fee;

    uint256 newX = x + ethAmountAfterFee;
    uint256 newY = k / newX;
    uint256 tokenAmount = y - newY;

    x = newX;
    y = newY;

    _mint(msg.sender, tokenAmount);

    emit Trade(TradeType.Buy, msg.sender, ethAmount, tokenAmount, fee);
  }

  function sell(uint256 tokenAmount) public payable nonReentrant {
    require(tokenAmount > 0, "Token amount must be greater than zero");

    uint256 fee = (tokenAmount * FEE_RATE) / 100;
    uint256 tokenAmountAfterFee = tokenAmount - fee;

    uint256 newY = y + tokenAmountAfterFee;
    uint256 newX = k / newY;
    uint256 ethAmount = x - newX;

    y = newY;
    x = newX;

    IERC20(this).transferFrom(msg.sender, address(this), tokenAmount);
    _burn(address(this), tokenAmountAfterFee);

    TransferUtil.safeTransferETH(msg.sender, ethAmount);

    emit Trade(TradeType.Sell, msg.sender, ethAmount, tokenAmount, fee);
  }

  function getExcessEth() public view returns (uint256) {
    uint256 ethAmount = x - initialX;
    return address(this).balance - ethAmount;
  }

  function getExcessToken() public view returns (uint256) {
    return balanceOf(address(this));
  }

  function getInfo() external view returns (SpaceInfo memory) {
    return SpaceInfo(name(), founder, creationId, sponsorCreationId);
  }

  // function withdrawExcessEth() external onlyFounder {
  //   uint256 excessEth = getExcessEth();
  //   require(excessEth > 0, "No excess ETH to withdraw");
  //   TransferUtil.safeTransferETH(space, excessEth);
  // }

  // function withdrawExcessToken() external onlyFounder {
  //   uint256 excessToken = getExcessToken();
  //   require(excessToken > 0, "No excess Token to withdraw");
  //   IERC20(this).transfer(space, excessToken);
  // }

  function upsertCollaborators(Share.UpsertCollaboratorInput[] calldata _collaborators) external {
    Share.upsertCollaborators(share, _collaborators);
  }

  function getCollaborators() public view returns (address[] memory, Share.Collaborator[] memory) {
    return Share.getCollaborators(share);
  }

  //================staking=======================

  function currentUserRewards(address user) public view returns (uint256) {
    return Staking.currentUserRewards(staking, user);
  }

  function currentRewardsPerToken() public view returns (uint256) {
    return Staking.currentRewardsPerToken(staking);
  }

  function stake(uint256 amount) public nonReentrant {
    return Staking.stake(staking, amount);
  }

  function unstake(uint256 amount) public nonReentrant {
    return Staking.unstake(staking, amount);
  }

  function claim() public nonReentrant returns (uint256) {
    return Staking.claim(staking);
  }

  function distribute() public {
    return Staking.distribute(staking);
  }

  function getStakingInfo() public view returns (Staking.Info memory) {
    return Staking.Info(staking.stakingFees, staking.totalStaked, staking.accumulatedRewardsPerToken);
  }
}
","keccak256":"0x30e4b10c08a771cc5b198e9e90be11edfe5fc447978fb392f09115e6a7abfdb2","license":"MIT"},"contracts/space/SpaceFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../interfaces/IIndieX.sol";
import "./Space.sol";

contract SpaceFactory is ERC1155Holder, Ownable, ReentrancyGuard {
  event Create(uint256 indexed spaceId, address creator, string spaceName, string symbol);

  address public immutable indieX;
  uint256 public spaceIndex = 0;
  mapping(address => address[]) public userSpaces;

  mapping(uint256 spaceId => address) public spaces;

  constructor(address initialOwner, address _indieX) Ownable(initialOwner) {
    indieX = _indieX;
  }

  function createSpace(
    string calldata spaceName,
    string calldata symbol,
    IIndieX.NewCreationInput calldata creationInput,
    IIndieX.NewCreationInput calldata sponsorCreationInput
  ) external {
    address founder = msg.sender;
    Space space = new Space(indieX, founder, spaceName, symbol);

    space.initialize(creationInput, sponsorCreationInput);

    spaces[spaceIndex] = address(space);
    userSpaces[msg.sender].push(address(space));
    emit Create(spaceIndex, founder, spaceName, symbol);

    spaceIndex++;
  }

  function newCreation(IIndieX.NewCreationInput memory input) public returns (uint256) {
    return IIndieX(indieX).newCreation(input);
  }

  function getUserSpaces(address user) public view returns (address[] memory) {
    return userSpaces[user];
  }

  // function getUserLatestSpace(address user) public view returns (Space.SpaceInfo memory info) {
  //   address[] memory spaceAddresses = userSpaces[user];
  //   if (spaceAddresses.length > 0) {
  //     address spaceAddress = spaceAddresses[spaceAddresses.length - 1];
  //     info = Space(payable(spaceAddress)).getInfo();
  //   }
  // }
}
