  string uri;
  address feeReceiver;
  uint256 feePercent; // subscription percent
}

struct CreateSpaceInput {
  uint256 appId;
  string spaceName;
  string symbol;
  string uri;
  uint256 preBuyEthAmount;
}

interface ISpaceFactory {
