pragma solidity ^0.8.20;

interface IIndieX {
  struct UpsertAppInput {
    string name;
    string uri;
    address feeTo;
    uint256 appFeePercent;
    uint256 creatorFeePercent;
  }

  struct App {
    uint256 id;
    address creator;
    string name;
    string uri;
    address feeTo;
    uint256 appFeePercent;
    uint256 creatorFeePercent;
  }

  struct NewCreationInput {
    uint256 appId;
    string name;
    string uri;
    uint256 curatorFeePercent;
    Curve curve;
    uint8 farmer;
    bool isFarming;
  }

  struct UpdateCreationInput {
    string name;
    string uri;
    uint256 curatorFeePercent;
  }

  struct Creation {
    uint256 id;
    uint256 appId;
    address creator;
    string name;
    string uri;
    uint256 curatorFeePercent;
    Curve curve;
    uint8 farmer;
    bool isFarming;
    uint256 balance;
    uint256 volume;
  }

  struct Curve {
    uint96 basePrice;
    uint32 inflectionPoint;
    uint128 inflectionPrice;
    uint128 linearPriceSlope;
  }

  struct PriceInfo {
    uint256 priceAfterFee;
    uint256 price;
    uint256 creatorFee;
    uint256 appFee;
    uint256 protocolFee;
  }

  event NewApp(
    uint256 id,
    address indexed creator,
    string name,
    string uri,
    address feeTo,
    uint256 appFeePercent,
    uint256 creatorFeePercent
  );

  event UpdateApp(
    uint256 id,
    address indexed creator,
    string name,
    string uri,
    address feeTo,
    uint256 appFeePercent,
    uint256 creatorFeePercent
  );

  event NewCreation(
    uint256 indexed creationId,
    address indexed creator,
    uint256 indexed appId,
    string name,
    string uri,
    Curve curve,
    uint8 farmerId,
    bool isFarming
  );

  event UpdateCreation(
    uint256 indexed creationId,
    address indexed creator,
    uint256 indexed appId,
    string name,
    string uri
  );

  event Trade(
    TradeType indexed tradeType,
    uint256 indexed creationId,
    address indexed sender,
    uint256 tokenAmount,
    uint256 fundAmount,
    uint256 creatorFee,
    uint256 appFee,
    uint256 protocolFee,
    address curator,
    uint256 curatorFee
  );

  event FarmerAdded(uint8 indexed farmerIndex, address indexed farmer);
  event ProtocolFeeToUpdated(address indexed previousFeeTo, address indexed newFeeTo);
  event ProtocolFeePercentUpdated(uint256 previousFeePercent, uint256 newFeePercent);

  enum TradeType {
    Mint,
    Buy,
    Sell
  }

  function newApp(UpsertAppInput memory input) external;

  function updateApp(uint256 id, UpsertAppInput memory input) external;

  function newCreation(NewCreationInput memory input) external returns (uint256);

  function updateCreation(uint256 id, UpdateCreationInput memory input) external;

  function buy(uint256 creationId, uint32 amount, address curator) external payable;

  function sell(uint256 creationId, uint32 amount) external;

  function getBuyPrice(uint256 creationId, uint32 amount) external view returns (uint256);

  function getSellPrice(uint256 creationId, uint32 amount) external view returns (uint256);

  function getBuyPriceAfterFee(
    uint256 creationId,
    uint32 amount,
    uint256 appId
  ) external view returns (PriceInfo memory);

  function getSellPriceAfterFee(
    uint256 creationId,
    uint32 amount,
    uint256 appId
  ) external view returns (PriceInfo memory);

  function getApp(uint256 id) external view returns (App memory);

  function getCreation(uint256 id) external view returns (Creation memory);

  function getUserCreations(address creator) external view returns (uint256[] memory);

  function creationSupply(uint256 id) external view returns (uint256);

  function getUserLatestCreation(address creator) external view returns (Creation memory creation);

  function getSubTotal(Creation memory creation, uint32 fromSupply, uint32 quantity) external pure returns (uint256);
}

contracts/lib/BondingCurveLib.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "solady/src/utils/FixedPointMathLib.sol";

library BondingCurveLib {
  function sigmoid2Sum(
    uint32 inflectionPoint,
    uint128 inflectionPrice,
    uint32 fromSupply,
    uint32 quantity
  ) internal pure returns (uint256 sum) {
    // We don't need checked arithmetic for the sum.
    // The max possible sum for the quadratic region is capped at:
    // `n * (n + 1) * (2*n + 1) * h < 2**32 * 2**33 * 2**34 * 2**128 = 2**227`.
    // The max possible sum for the sqrt region is capped at:
    // `end * (2*h * sqrt(end)) < 2**32 * 2**129 * 2**16 = 2**177`.
    // The overall sum is capped by:
    // `2**161 + 2**227 <= 2**228 < 2 **256`.
    // The result will be small enough for unchecked multiplication with a 16-bit BPS.
    unchecked {
      uint256 g = inflectionPoint;
      uint256 h = inflectionPrice;

      // Early return to save gas if either `g` or `h` is zero.
      if (g * h == 0) return 0;

      uint256 s = uint256(fromSupply) + 1;
      uint256 end = s + uint256(quantity);
      uint256 quadraticEnd = FixedPointMathLib.min(g, end);

      if (s < quadraticEnd) {
        uint256 k = uint256(fromSupply); // `s - 1`.
        uint256 n = quadraticEnd - 1;
        // In practice, `h` (units: wei) will be set to be much greater than `g * g`.
        uint256 a = FixedPointMathLib.rawDiv(h, g * g);
        // Use the closed form to compute the sum.
        sum = ((n * (n + 1) * ((n << 1) + 1) - k * (k + 1) * ((k << 1) + 1)) / 6) * a;
        s = quadraticEnd;
      }

      if (s < end) {
        uint256 c = (3 * g) >> 2;
        uint256 h2 = h << 1;
        do {
          uint256 r = FixedPointMathLib.sqrt((s - c) * g);
          sum += FixedPointMathLib.rawDiv(h2 * r, g);
        } while (++s != end);
      }
    }
  }

  function linearSum(uint128 linearPriceSlope, uint32 fromSupply, uint32 quantity) internal pure returns (uint256 sum) {
    // We don't need checked arithmetic for the sum because the max possible
    // intermediate value is capped at:
    // `k * m < 2**32 * 2**128 = 2**160 < 2**256`.
    // As `quantity` is 32 bits, max possible value for `sum`
    // is capped at:
    // `2**32 * 2**160 = 2**192 < 2**256`.
    // The result will be small enough for unchecked multiplication with a 16-bit BPS.
    unchecked {
      uint256 m = linearPriceSlope;
      uint256 k = uint256(fromSupply);
      uint256 n = k + uint256(quantity);
      // Use the closed form to compute the sum.
      return m * ((n * (n + 1) - k * (k + 1)) >> 1);
    }
  }
}

contracts/space/Space.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../interfaces/IIndieX.sol";
import "./Token.sol";

contract Space is ERC1155Holder, ReentrancyGuard {
  using SafeERC20 for IERC20;

  uint256 public constant PER_TOKEN_PRECISION = 10 ** 18;
  string public name;
  string public symbol;
  address token;
  uint256 public creationId;
  uint256 public sponsorCreationId;

  uint256 public daoFeePercent = 0.5 ether; // 50%

  uint256 founderAccumulated = 0;

  uint256 public stakingFees = 0; // fee for rewards
  uint256 public daoFees = 0; // fee for rewards

  ERC20 public stakingToken; // Token to be staked
  uint256 public totalStaked; // Total amount staked
  mapping(address => uint256) public userStake; // Amount staked per user

  uint256 public accumulatedRewardsPerToken = 0;

  mapping(address => UserRewards) public accumulatedRewards; // Rewards accumulated per user

  event Staked(address user, uint256 amount);
  event Unstaked(address user, uint256 amount);
  event Claimed(address user, uint256 amount);
  event RewardsPerTokenUpdated(uint256 accumulated);
  event UserRewardsUpdated(address user, uint256 rewards, uint256 checkpoint);
  event Received(address sender, uint256 daoFee, uint256 stakingFee);

  struct SpaceInfo {
    string name;
    string symbol;
    address token;
    uint256 creationId;
    uint256 sponsorCreationId;
  }

  struct UserRewards {
    uint256 accumulated; // realized reward token amount
    // checkpoint to compare with RewardsPerToken.accumulated
    uint256 checkpoint;
  }

  address public immutable founder;

  constructor(address _founder) {
    founder = _founder;
  }

  modifier onlyFounder() {
    require(msg.sender == founder, "Only Founder");
    _;
  }

  fallback() external payable {}

  receive() external payable {
    uint256 fees = msg.value;
    uint256 daoFee = (fees * daoFeePercent) / 1 ether;
    uint256 stakingFee = fees - daoFee;
    daoFees += daoFee;
    stakingFees += stakingFee;
    emit Received(msg.sender, daoFee, stakingFees);
  }

  function setDaoFeePercent(uint256 _daoFeePercent) external onlyFounder {
    daoFeePercent = _daoFeePercent;
  }

  function withdrawExcessEth() external onlyFounder {
    _safeTransferETH(founder, daoFees);
    daoFees = 0;
  }

  function create(
    address indieX,
    string calldata _spaceName,
    string calldata _symbol,
    IIndieX.NewCreationInput memory creationInput,
    IIndieX.NewCreationInput memory sponsorCreationInput
  ) external {
    name = _spaceName;
    symbol = _symbol;

    Token _token = new Token(founder, _spaceName, _symbol);
    token = address(_token);

    stakingToken = _token;

    creationId = IIndieX(indieX).newCreation(creationInput);
    sponsorCreationId = IIndieX(indieX).newCreation(sponsorCreationInput);
  }

  function getInfo() external view returns (SpaceInfo memory) {
    return SpaceInfo(name, symbol, token, creationId, sponsorCreationId);
  }

  function _calculateRewardsPerToken() internal view returns (uint256) {
    if (totalStaked == 0) return accumulatedRewardsPerToken;
    return accumulatedRewardsPerToken + (PER_TOKEN_PRECISION * stakingFees) / totalStaked;
  }

  /// @notice Calculate the rewards accumulated by a stake between two checkpoints.
  function _calculateUserRewards(
    uint256 stake_,
    uint256 earlierCheckpoint,
    uint256 latterCheckpoint
  ) internal pure returns (uint256) {
    return (stake_ * (latterCheckpoint - earlierCheckpoint)) / PER_TOKEN_PRECISION;
  }

  function _updateRewardsPerToken() internal returns (uint256) {
    uint256 rewardsPerTokenOut = _calculateRewardsPerToken();

    bool isChanged = accumulatedRewardsPerToken != rewardsPerTokenOut;

    if (isChanged) {
      stakingFees = 0;
    }

    accumulatedRewardsPerToken = rewardsPerTokenOut;

    emit RewardsPerTokenUpdated(rewardsPerTokenOut);

    return rewardsPerTokenOut;
  }

  function _updateUserRewards(address user) internal returns (UserRewards memory) {
    _updateRewardsPerToken();
    UserRewards memory userRewards_ = accumulatedRewards[user];

    // We skip the storage changes if already updated in the same block
    if (userRewards_.checkpoint == accumulatedRewardsPerToken) return userRewards_;

    // Calculate and update the new value user reserves.
    userRewards_.accumulated += _calculateUserRewards(
      userStake[user],
      userRewards_.checkpoint,
      accumulatedRewardsPerToken
    );

    userRewards_.checkpoint = accumulatedRewardsPerToken;

    accumulatedRewards[user] = userRewards_;
    emit UserRewardsUpdated(user, userRewards_.accumulated, userRewards_.checkpoint);

    return userRewards_;
  }

  /// @notice Stake tokens.
  function stake(uint256 amount) public virtual nonReentrant {
    address user = msg.sender;
    _updateUserRewards(user);
    totalStaked += amount;
    userStake[user] += amount;
    IERC20(stakingToken).safeTransferFrom(user, address(this), amount);
    emit Staked(user, amount);
  }

  /// @notice Unstake tokens.
  function unstake(uint256 amount) public virtual nonReentrant {
    address user = msg.sender;
    _updateUserRewards(user);
    totalStaked -= amount;
    userStake[user] -= amount;
    IERC20(stakingToken).safeTransfer(user, amount);
    emit Unstaked(user, amount);
  }

  /// @notice Claim all rewards for the caller.
  function claim() public virtual nonReentrant returns (uint256) {
    address user = msg.sender;
    _updateUserRewards(user);

    uint256 amount = accumulatedRewards[user].accumulated;
    _safeTransferETH(user, amount);

    accumulatedRewards[user].accumulated = 0;
    emit Claimed(user, amount);
    return amount;
  }

  function distribute() public virtual {
    _updateRewardsPerToken();
  }

  /// @notice Calculate and return current rewards per token.
  function currentRewardsPerToken() public view returns (uint256) {
    return _calculateRewardsPerToken();
  }

  /// @notice Calculate and return current rewards for a user.
  /// @dev This repeats the logic used on transactions, but doesn't update the storage.
  function currentUserRewards(address user) public view returns (uint256) {
    UserRewards memory accumulatedRewards_ = accumulatedRewards[user];

    uint256 currentAccumulatedRewardsPerToken = _calculateRewardsPerToken();

    uint256 rewards = accumulatedRewards_.accumulated +
      _calculateUserRewards(userStake[user], accumulatedRewards_.checkpoint, currentAccumulatedRewardsPerToken);

    return rewards;
  }

  function _safeTransferETH(address to, uint256 value) internal {
    (bool success, ) = to.call{ value: value }("");
    require(success, "ETH transfer failed");
  }
}

